// Realistic Apple - Medium Complexity
// Designed for 3D printing with proper wall thickness

// Overall dimensions
apple_radius = 25;
stem_length = 12;
stem_radius = 2;

// Apple body with indentations
module apple_body() {
    // Main apple shape - slightly flattened sphere
    scale([1, 1, 0.9])
    sphere(r = apple_radius, $fn = 64);

    // Top indentation for stem
    translate([0, 0, apple_radius * 0.9])
    rotate_extrude(angle = 360, $fn = 32)
    translate([0, 0])
    polygon(points = [[0, 0], [apple_radius * 0.2, -apple_radius * 0.3], [0, -apple_radius * 0.3]]);

    // Bottom indentation
    translate([0, 0, -apple_radius * 0.9])
    rotate_extrude(angle = 360, $fn = 32)
    translate([0, 0])
    polygon(points = [[0, 0], [apple_radius * 0.15, apple_radius * 0.2], [0, apple_radius * 0.2]]);
}

// Apple stem with slight curve
module apple_stem() {
    // Main stem body
    translate([0, 0, apple_radius * 0.9 + stem_length * 0.5])
    rotate([0, 5, 0])
    cylinder(h = stem_length, r = stem_radius, $fn = 16);

    // Stem base flare
    translate([0, 0, apple_radius * 0.9])
    cylinder(h = 2, r1 = stem_radius * 1.5, r2 = stem_radius, $fn = 16);
}

// Apple leaf
module apple_leaf() {
    translate([stem_radius * 1.5, 0, apple_radius * 0.9 + stem_length * 0.8])
    rotate([0, 30, 45])
    scale([1, 0.6, 0.1])
    sphere(r = 8, $fn = 32);

    // Leaf stem
    translate([stem_radius * 1.5, 0, apple_radius * 0.9 + stem_length * 0.8])
    rotate([0, 30, 45])
    translate([8, 0, 0])
    cylinder(h = 4, r = 0.5, $fn = 8);
}

// Apple calyx (bottom star pattern)
module apple_calyx() {
    difference() {
        // Base shape
        translate([0, 0, -apple_radius * 0.85])
        cylinder(h = 2, r = apple_radius * 0.15, $fn = 32);

        // Star pattern cuts
        for(i = [0:4]) {
            rotate([0, 0, i * 72])
            translate([apple_radius * 0.08, 0, -apple_radius * 0.87])
            cube([apple_radius * 0.12, 2, 4], center = true);
        }
    }
}

// Apple surface details - subtle bumps
module apple_surface() {
    // No actual surface details for 3D printing simplicity
    // Could add bump texture here if needed
}

// Main apple assembly
union() {
    // Main apple body
    apple_body();

    // Stem assembly
    union() {
        apple_stem();
        apple_leaf();
    }

    // Calyx at bottom
    apple_calyx();

    // Optional: Small imperfections for realism
    for(i = [0:3]) {
        rotate([0, 0, i * 90 + 15])
        translate([apple_radius * 0.7, 0, apple_radius * 0.2])
        sphere(r = 1.5, $fn = 8);
    }
}

// Realistic Pear - Medium Complexity
// 3D printable, ~70mm height

pear_size = 70;
stem_length = 8;
stem_diameter = 3;

module pear_body() {
    // Main pear body - combination of spheres with tapering
    union() {
        // Upper bulb (shoulders)
        translate([0, 0, 25])
            scale([1, 1, 0.8])
            sphere(d=35, $fn=60);

        // Lower bulb (bulbous bottom)
        translate([0, 0, 8])
            scale([1.2, 1.2, 1.1])
            sphere(d=40, $fn=60);

        // Tapering bottom
        translate([0, 0, -5])
            scale([0.7, 0.7, 0.6])
            sphere(d=30, $fn=60);
    }
}

module stem() {
    // Main stem cylinder
    translate([0, 0, 38])
        cylinder(h=stem_length, d=stem_diameter, $fn=20);

    // Stem flare at base
    translate([0, 0, 38])
        cylinder(h=2, d1=stem_diameter*1.5, d2=stem_diameter, $fn=20);
}

module leaf() {
    // Single leaf shape using scaled sphere and cube
    translate([0, 12, 42])
        rotate([0, -20, 30])
        union() {
            // Leaf body
            scale([2, 0.8, 0.1])
                sphere(d=8, $fn=30);

            // Leaf tip extension
            translate([8, 0, 0])
                scale([0.5, 0.5, 0.1])
                sphere(d=6, $fn=20);
        }
}

module pear_detail() {
    // Vertical indent lines for texture
    for(i=[0:15:360]) {
        rotate([0, 0, i])
            translate([17, 0, 0])
                scale([0.1, 1, 1])
                cube([1, 0.5, 60], center=true);
    }

    // Small indent at bottom
    translate([0, 0, -5])
        rotate_extrude($fn=30)
            translate([8, 0, 0])
                circle(d=3, $fn=20);
}

// Main pear assembly
union() {
    pear_body();
    stem();
    leaf();
    pear_detail();
}

// Flatten bottom for printing
translate([0, 0, -7])
    cube([40, 40, 2], center=true);

// Peach 3D Model - Realistic Style, Medium Complexity
// Size: ~70mm tall

module peach_body() {
    // Main peach body - slightly flattened sphere for realistic shape
    translate([0, 0, 5]) 
        scale([1.1, 1, 0.9]) 
        sphere(d=55, $fn=64);

    // Bottom indentation
    translate([0, 0, -25])
        rotate([0, 0, 0])
        cylinder(h=10, d1=15, d2=8, $fn=32);
}

module peach_crease() {
    // Vertical crease line
    difference() {
        peach_body();
        translate([0, -27, 5])
            rotate([0, 90, 0])
            cylinder(h=60, d=3, $fn=32);
    }
}

module stem() {
    // Stem base
    translate([0, 0, 30])
        rotate([20, 0, 0])
        cylinder(h=8, d=8, $fn=16);

    // Stem extension
    translate([0, 0, 35])
        rotate([30, 0, 0])
        cylinder(h=12, d=5, $fn=16);

    // Small leaf
    translate([5, 5, 40])
        rotate([45, 30, 45])
        scale([1, 0.3, 1])
        sphere(d=8, $fn=16);
}

module dimples() {
    // Create realistic peach dimples using small spheres
    for(a = [0:30:150]) {
        for(b = [30:30:150]) {
            rotate([a, b, 0])
            translate([0, 0, 25])
                difference() {
                    sphere(d=55, $fn=64);
                    translate([sin(a)*cos(b)*25, sin(a)*sin(b)*25, cos(a)*25])
                        sphere(d=8, $fn=16);
                }
        }
    }
}

module realistic_peach() {
    // Combine all parts
    union() {
        // Main body with crease
        peach_crease();

        // Add stem
        stem();

        // Add subtle dimples for texture
        difference() {
            sphere(d=55, $fn=64);
            dimples();
        }
    }
}

// Render the complete peach
scale([1, 1, 1])
realistic_peach();

// Realistic Plum - Medium Complexity
// Designed for 3D printing at ~60mm height

// Main plum body
module plum_body() {
    // Base ellipsoid shape
    difference() {
        union() {
            // Main body - slightly flattened sphere
            scale([1, 1.2, 0.9])
            sphere(d=40, $fn=64);

            // Bottom bulge for realistic plum shape
            translate([0, 0, -18])
            scale([0.8, 1, 0.6])
            sphere(d=25, $fn=32);

            // Top depression
            translate([0, 0, 18])
            scale([0.7, 0.7, 0.3])
            sphere(d=15, $fn=32);
        }

        // Stem cavity
        translate([0, 0, 20])
        cylinder(h=8, d1=4, d2=2, $fn=16);

        // Natural seam groove
        rotate([0, 90, 0])
        translate([0, 0, -20])
        scale([0.05, 1, 1])
        cube([40, 40, 40], center=true);
    }
}

// Stem
module stem() {
    translate([0, 0, 20])
    rotate([0, 0, rand(5, 15)])
    cylinder(h=12, d1=2.5, d2=1.5, $fn=8);
}

// Leaf
module leaf() {
    translate([0, 8, 20])
    rotate([0, -20, 0])
    scale([1, 0.6, 0.1])
    sphere(d=12, $fn=32);

    // Leaf vein
    translate([0, 8, 20.5])
    rotate([0, -20, 0])
    scale([0.05, 0.4, 0.01])
    cube([10, 10, 1], center=true);
}

// Surface bumps for realistic texture
module surface_texture() {
    for(i = [0:15]) {
        rotate([rand(0, 360), rand(0, 360), rand(0, 360)])
        translate([
            rand(-18, 18),
            rand(-18, 18),
            rand(-15, 15)
        ]) {
            scale([1, 1, 0.7])
            sphere(d=rand(2, 4), $fn=8);
        }
    }
}

// Bloom (natural powdery coating)
module bloom() {
    difference() {
        scale([1.02, 1.02, 1.02])
        plum_body();
        plum_body();
    }
}

// Bloom surface texture
module bloom_texture() {
    %surface_texture();
}

// Helper function for random values
function rand(min, max) = min + (max - min) * (rands(0, 1, 1)[0]);

// Assembly
union() {
    // Main plum with hollow interior for printing
    difference() {
        plum_body();
        scale([0.9, 0.9, 0.9])
        translate([0, 0, -2])
        plum_body();
    }

    // Surface details
    union() {
        stem();
        leaf();
        surface_texture();
    }

    // Light bloom coating
    bloom();
}

// Cherry with stem - 3D printable medium complexity realistic style
// Overall size ~55 mm tall

$fn = 64;  // Smooth surfaces

// Main cherry body
module cherry_body() {
    // Core sphere
    sphere(20);

    // Slight bulge at bottom for realistic shape
    translate([0,0,-3])
        scale([1.1,1.1,0.7])
            sphere(20);

    // Small indent at top for stem
    translate([0,0,19])
        rotate_extrude()
            translate([8,0,0])
                circle(r=3);
}

// Stem structure
module stem() {
    // Main stem
    translate([0,0,20])
        rotate([0,15,0])
            cylinder(h=35, r1=2.5, r2=1.5);

    // Stem tip
    translate([0,0,54.5])
        rotate([0,15,0])
            sphere(1.5);

    // Small leaf
    translate([5,0,45])
        rotate([0,15,-30])
            scale([1,0.6,0.1])
                sphere(8);
}

// Surface details - small bumps and dimples
module cherry_surface() {
    for (i = [0:30]) {
        rotate([0, i*12, i*7.5])
            translate([20,0,0])
                sphere(0.5);
    }

    // Tiny indent at bottom
    translate([0,0,-20])
        sphere(1);
}

// Create full cherry
union() {
    // Main body with surface details
    difference() {
        union() {
            cherry_body();
            cherry_surface();
        }

        // Remove indent for stem connection
        translate([0,0,19])
            cylinder(h=5, r=2.5);
    }

    // Add stem
    stem();
}

// Optional: Flatten bottom slightly for printing stability
translate([0,0,-21])
    cube([40,40,1], center=true);

// Realistic apricot, medium complexity
// Designed for 3D printing, no overhangs, 50 mm tall

$fn = 32;

// Main apricot body
module apricot_body() {
    difference() {
        // Main sphere slightly squashed
        scale([1.05, 1, 0.9])
        sphere(d=45);

        // Stem cavity
        translate([0, 0, 20])
        rotate([0, 0, 0])
        cylinder(h=8, d1=6, d2=3, $fn=16);

        // Bottom indent
        translate([0, 0, -20.5])
        sphere(d=8);
    }
}

// Surface indentations for realism
module surface_detail() {
    for (i = [0:15]) {
        rotate([0, 0, i*24])
        translate([18, 0, 0])
        rotate([90, 0, 0])
        scale([1, 1, 0.3])
        sphere(d=3);
    }

    for (j = [0:11]) {
        rotate([0, 0, j*30 + 15])
        translate([12, 0, 0])
        rotate([90, 0, 0])
        scale([1, 1, 0.3])
        sphere(d=2.5);
    }
}

// Stem
module stem() {
    translate([0, 0, 20.5])
    rotate([0, 0, 5])
    cylinder(h=12, d1=2.5, d2=1.5, $fn=8);
}

// Leaf
module leaf() {
    translate([0, 8, 21])
    rotate([0, 110, -10])
    scale([0.4, 1, 0.1])
    difference() {
        sphere(d=15);
        translate([0, -8, 0])
        scale([1, 1, 10])
        cube([20, 8, 2], center=true);
    }
}

// Crease line
module crease() {
    translate([0, -22.5, 0])
    rotate([90, 0, 0])
    scale([1, 0.8, 0.05])
    cylinder(h=45, d=45);
}

// Assembly
union() {
    difference() {
        apricot_body();
        surface_detail();
    }
    stem();
    leaf();
    crease();
}
