// Gladius Hispaniensis - Mainz type (1st century AD)
// Overall length ~85 mm for 3D printing (1:10 scale)

blade_length = 65;
blade_width = 9;
blade_thickness = 2;
handle_length = 18;
guard_width = 12;
guard_thickness = 3;
pommel_diameter = 10;
pommel_height = 5;

// Main blade construction
module gladius_blade() {
    color("silver")
    union() {
        // Central fullered blade
        difference() {
            // Main blade body with slight taper
            hull() {
                translate([0, 0, 0])
                    cube([blade_length, blade_width, blade_thickness], center=true);
                translate([blade_length*0.8, 0, 0])
                    scale([0.3, 0.3, 1])
                        cube([blade_length*0.2, blade_width, blade_thickness], center=true);
            }

            // Fuller grooves (two parallel grooves)
            for(side = [-1, 1]) {
                translate([blade_length*0.3, side*blade_width*0.25, -blade_thickness/2])
                    rotate([0, 0, 0])
                        scale([1, 0.2, 0.8])
                            cylinder(h=blade_thickness*2, r=blade_width*0.15, $fn=8);
            }

            // Point shaping
            translate([blade_length*0.9, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h=blade_width*1.5, r=blade_width, $fn=3);
        }
    }
}

// Decorative guard with classical motifs
module gladius_guard() {
    color("bronze")
    difference() {
        // Main guard body
        translate([-guard_thickness/2, 0, 0])
            cube([guard_thickness, guard_width, blade_thickness*1.5], center=true);

        // Decorative cutouts
        for(i = [1:3]) {
            translate([-guard_thickness/2, -guard_width*0.3 + i*guard_width*0.3, 0])
                rotate([90, 0, 0])
                    cylinder(h=guard_thickness+1, r=1.5, $fn=6);
        }
    }
}

// Grip with wire wrapping texture
module gladius_grip() {
    color("brown")
    difference() {
        // Main grip cylinder
        translate([-handle_length/2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=handle_length, r=blade_thickness*1.2, $fn=16);

        // Wire wrapping grooves
        for(i = [0:5]) {
            translate([-handle_length*0.8 + i*handle_length*0.15, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h=handle_length*0.05, r=blade_thickness*1.25, $fn=16);
        }
    }
}

// Pommel with typical bulbous shape
module gladius_pommel() {
    color("bronze")
    union() {
        // Main pommel
        translate([-handle_length - pommel_height/2, 0, 0])
            scale([1, 1.2, 1.2])
                sphere(r=pommel_diameter/2, $fn=16);

        // Decorative ring
        translate([-handle_length - pommel_height*0.7, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=pommel_height*0.3, r1=pommel_diameter*0.6, r2=pommel_diameter*0.5, $fn=16);
    }
}

// Assembly
module gladius_complete() {
    union() {
        gladius_blade();
        translate([blade_length*0.45, 0, 0])
            gladius_guard();
        translate([blade_length*0.45 + guard_thickness/2, 0, 0])
            gladius_grip();
        translate([blade_length*0.45 + guard_thickness/2 + handle_length, 0, 0])
            gladius_pommel();
    }
}

// Render the gladius
gladius_complete();

$fa = 0.5;
$fs = 0.5;

// Scutum (Roman rectangular curved shield)
// Overall dimensions: 80mm tall, 45mm wide, 8mm thick
color("chocolate") difference() {
    // Main curved shield body
    translate([0, -5, 0])
    scale([1, 1.2, 1])
    rotate([0, 10, 0])
    cube([45, 80, 7], center = true);

    // Cut out central boss hole
    translate([0, 0, 4])
    cylinder(h = 8, r1 = 7, r2 = 5);

    // Cut out hand grip slots
    translate([0, -15, 4])
    cube([8, 20, 8], center = true);
}

// Central boss (umbo)
color("goldenrod") translate([0, 0, 5])
difference() {
    cylinder(h = 8, r1 = 11, r2 = 8);
    // Boss hollow
    translate([0, 0, -1])
    cylinder(h = 10, r1 = 7, r2 = 4);
}

// Decorative spine along vertical axis
color("saddlebrown") for(i = [-1:1]) {
    translate([i * 2, 0, 3.5])
    cube([1, 78, 2]);
}

// Horizontal reinforcing strips
color("saddlebrown") for(y = [-25, 0, 25]) {
    translate([0, y, 3.5])
    cube([44, 2, 2]);
}

// Rivet heads along edges
color("goldenrod") for(y = [-35:10:35]) {
    translate([20, y, 6])
    sphere(r = 1);
    translate([-20, y, 6])
    sphere(r = 1);
}

// Handle grip behind boss
color("saddlebrown") translate([0, -15, -2])
rotate([90, 0, 0])
difference() {
    cylinder(h = 25, r = 3);
    translate([0, 0, -1])
    cylinder(h = 27, r = 1.5);
}

// Decorative central motif
color("goldenrod") translate([0, 0, 7])
scale([1, 0.8, 1])
rotate([0, 0, 45])
difference() {
    cube([10, 10, 1], center = true);
    cube([6, 6, 2], center = true);
}

// Wing motifs on sides
color("goldenrod") for(side = [-1, 1]) {
    translate([side * 22, 15, 6.5])
    rotate([0, 0, side * -30])
    scale([1, 0.5, 1])
    cylinder(h = 1, r = 5);

    translate([side * 22, -15, 6.5])
    rotate([0, 0, side * 30])
    scale([1, 0.5, 1])
    cylinder(h = 1, r = 5);
}

// Edge reinforcement
color("saddlebrown") difference() {
    // Outer frame
    translate([0, 0, 3.5])
    cube([47, 82, 3], center = true);

    // Inner cutout
    translate([0, 0, 2])
    cube([43, 78, 5], center = true);
}

// Roman Pilum - Medium Complexity Historical Artifact
// 3D printable model with realistic proportions

$fn = 64;

// Main pilum module
module pilum() {
    // Iron shaft with pyramidal tip
    color("silver") {
        // Pyramidal head
        translate([0, 0, 45])
            rotate([0, 0, 45])
            pyramid_head();

        // Main iron shaft
        translate([0, 0, 0])
            cylinder(h=45, d1=3, d2=2.5);

        // Shaft reinforcement ring
        translate([0, 0, 38])
            cylinder(h=3, d=4);
    }

    // Wooden shaft socket
    color("saddlebrown") {
        translate([0, 0, -30])
            cylinder(h=30, d1=4, d2=3, $fn=8);
    }

    // Bronze ferrule at base
    color("bronze") {
        translate([0, 0, -31])
            cylinder(h=4, d=5, $fn=8);
    }

    // Socket rivets (3 evenly spaced)
    color("silver") {
        for(i = [0:2]) {
            rotate([0, 0, i * 120])
                translate([2.2, 0, -25])
                    cylinder(h=6, d=0.8);
        }
    }

    // Decorative grip rings
    color("bronze") {
        for(pos = [-20, -15, -10]) {
            translate([0, 0, pos])
                cylinder(h=1.5, d=4.5);
        }
    }

    // Weighted counterbalance
    color("silver") {
        translate([0, 0, -35])
            sphere(d=5);
    }
}

// Pyramidal head module
module pyramid_head() {
    difference() {
        // Main pyramid
        linear_extrude(height=15, scale=0.01)
            square([3, 3], center=true);

        // Hollow core for weight reduction
        translate([0, 0, 0])
            linear_extrude(height=15, scale=0.01)
                square([2.5, 2.5], center=true);
    }
}

// Scale to reasonable 3D print size (80mm total length)
scale([1, 1, 1]) {
    pilum();
}

// Kopis Sword - Historical Greek Artifact
// 3D printable model with realistic features

// Color definitions
blade_color = [0.7, 0.7, 0.8];  // Steel
handle_color = [0.4, 0.3, 0.2]; // Dark wood
guard_color = [0.6, 0.5, 0.4];  // Bronze

// Overall dimensions
blade_length = 65;
blade_width = 28;
handle_length = 20;
guard_width = 12;
total_length = blade_length + handle_length;

module kopis() {
    // Main blade with forward curve
    color(blade_color) {
        // Blade body - curved forward
        translate([0, 0, 2]) {
            difference() {
                // Main blade shape
                hull() {
                    translate([-blade_length/2, 0, 0]) 
                        cube([blade_length, blade_width, 4], center=true);
                    translate([blade_length/2 - 10, 0, 0]) 
                        cube([20, blade_width/3, 4], center=true);
                }

                // Fuller groove
                translate([0, 0, 1]) {
                    for(i = [-20:10:20]) {
                        rotate([0, 0, 5]) 
                            translate([i, 0, -1]) 
                                cylinder(h=6, r=1.5, $fn=8);
                    }
                }

                // Edge bevel
                translate([blade_length/2 - 15, blade_width/2 - 8, 0])
                    cube([30, 8, 6], center=true);
            }
        }

        // Tip curve
        translate([blade_length/2 - 8, 0, 2]) {
            rotate([0, 0, -45])
                cylinder(h=8, r1=3, r2=0, $fn=8);
        }
    }

    // Guard
    color(guard_color) {
        translate([-blade_length/2 - 5, 0, 0]) {
            difference() {
                // Main guard shape
                hull() {
                    cube([guard_width, blade_width/1.5, 6], center=true);
                    translate([-guard_width/2, 0, 0])
                        cube([guard_width/2, blade_width/1.3, 6], center=true);
                }

                // Central hole
                translate([-guard_width/2 - 2, 0, 0])
                    cylinder(h=8, r=3, center=true);
            }
        }
    }

    // Handle
    color(handle_color) {
        translate([-blade_length/2 - handle_length/2 - 5, 0, 0]) {
            // Handle grip
            difference() {
                cylinder(h=handle_length, r=4, center=true, $fn=8);

                // Grip grooves
                for(i = [-handle_length/2 + 2:3:handle_length/2 - 2]) {
                    translate([0, 0, i])
                        cylinder(h=1, r=3.5, center=true, $fn=8);
                }
            }

            // Pommel
            translate([0, 0, -handle_length/2 - 4]) {
                cylinder(h=8, r1=3, r2=5, $fn=16);

                // Decorative ring
                translate([0, 0, -2])
                    cylinder(h=2, r=5.5, r1=5.5, $fn=32);
            }
        }
    }

    // Decorative inscriptions on blade
    color([0.5, 0.5, 0.6]) {
        translate([0, blade_width/4, 2.5]) {
            rotate([0, 0, 5]) {
                for(i = [0:3]) {
                    translate([-blade_length/3 + i*8, 0, 0])
                        cube([1, 0.5, 1]);
                }
            }
        }
    }
}

// Render the kopis
kopis();

// Greek Xiphos - Iron Age short sword
// Historically accurate medium-complexity model

// Xiphos dimensions (mm)
blade_len = 70;
blade_width = 20;
handle_len = 35;
guard_width = 30;
pommel_d = 12;

// Blade module with leaf shape
module leaf_blade() {
    // Blade tapering towards tip
    hull() {
        // Base section
        translate([0, 0, -1])
            scale([1, 1, 2])
                cylinder(h=2, d1=blade_width, d2=blade_width-2, $fn=4);

        // Mid section
        translate([blade_len/3, 0, 0])
            scale([1, 1, 1.8])
                cylinder(h=2, d1=blade_width-4, d2=blade_width-6, $fn=4);

        // Tip section
        translate([blade_len-2, 0, 0])
            cylinder(h=2, d1=blade_width-8, d2=1, $fn=3);
    }

    // Fullers (blood grooves)
    for(i = [0:1]) {
        translate([blade_len/2 + i*15, 0, 1])
            rotate([90, 0, 0])
                cylinder(h=blade_len/2, d=2, $fn=6);
    }
}

// Guard with decorative elements
module guard() {
    difference() {
        // Main guard
        translate([-3, 0, 0])
            cube([6, guard_width, 8]);

        // Decorative cutouts
        for(i = [-1:1]) {
            translate([0, i*8, 2])
                cylinder(h=9, d=4, $fn=6);
        }

        // Central hole
        translate([0, 0, -1])
            cylinder(h=10, d=6, $fn=20);
    }
}

// Handle with spiral wrap
module handle() {
    // Core
    color("saddlebrown")
    cylinder(h=handle_len, d=10, $fn=8);

    // Leather wrap rings
    color("darkgoldenrod")
    for(i = [2:4:handle_len-6]) {
        translate([0, 0, i])
            cylinder(h=2, d=11, $fn=20);
    }

    // Rivets
    color("darkgoldenrod")
    for(i = [1:3]) {
        rotate([0, 0, i*120])
            translate([5, 0, handle_len/2])
                cylinder(h=10, d=2, $fn=6);
    }
}

// Pommel with typical xiphos shape
module pommel() {
    // Main pommel
    color("saddlebrown")
    difference() {
        sphere(d=pommel_d, $fn=20);
        // Flattened sides
        translate([0, 0, -pommel_d/2])
            cube([pommel_d, pommel_d, pommel_d], center=true);
    }

    // Decorative cap
    color("goldenrod")
    translate([0, 0, pommel_d/2])
        cylinder(h=2, d=pommel_d-2, $fn=20);
}

// Assembly
module xiphos() {
    // Blade
    color("silver")
    translate([0, 0, 4])
        leaf_blade();

    // Guard
    color("goldenrod")
    translate([-3, 0, 0])
        guard();

    // Handle
    translate([0, 0, -handle_len])
        handle();

    // Pommel
    translate([0, 0, -handle_len-2])
        pommel();
}

// Render the complete xiphos
xiphos();

// Historical Dory - Bronze Age Greek Fishing Vessel
// Scale: 1:50, Length: 90mm, Width: 20mm, Height: 12mm
// Based on 8th century BCE Mediterranean fishing dory

$fn = 32;

// Main hull with characteristic pointed bow and flat stern
module hull() {
    color("burlywood") difference() {
        // Main hull body - elongated with tapered ends
        hull_shape();

        // Interior cavity for hollow printing
        translate([0, 0, 2]) scale([0.85, 0.85, 0.8]) hull_shape();
    }
}

// Basic hull form
module hull_shape() {
    hull_points = [
        [0, 0], [10, 8], [25, 12], [45, 15], 
        [70, 15], [85, 12], [90, 8], [90, 0]
    ];

    linear_extrude(height = 12, convexity = 10) {
        polygon(points = hull_points);
    }
}

// Decorative sternpost with traditional spiral carving
module sternpost() {
    color("burlywood") union() {
        // Main sternpost
        translate([88, 7.5, 8]) rotate([0, 90, 0]) 
            cylinder(h = 4, d1 = 3, d2 = 2);

        // Spiral decoration
        for (i = [0:3]) {
            rotate([0, 0, i * 90]) 
                translate([88.5, 7.5, 10 - i*0.5]) 
                    cylinder(h = 0.5, d = 1.5);
        }
    }
}

// Rowing benches with traditional cross-bracing
module benches() {
    color("saddlebrown") 
    for (pos = [25, 45, 65]) {
        translate([pos, 0, 10]) cube([8, 18, 1]);
        // Support struts
        translate([pos + 2, 8, 9]) cube([1, 1, 1]);
        translate([pos + 2, -9, 9]) cube([1, 1, 1]);
    }
}

// Bronze age oars with carved handles
module oars() {
    color("goldenrod") 
    for (i = [0:3]) {
        // Port oars
        translate([20 + i*15, 10, 9]) rotate([0, 0, -45]) {
            cylinder(h = 12, d = 0.8);
            translate([0, 12, 0]) sphere(d = 1.2);
        }
        // Starboard oars
        translate([20 + i*15, -10, 9]) rotate([0, 0, 45]) {
            cylinder(h = 12, d = 0.8);
            translate([0, -12, 0]) sphere(d = 1.2);
        }
    }
}

// Bronze eye of protection on bow
module protective_eye() {
    color("gold") difference() {
        translate([3, 7.5, 10]) sphere(d = 2.5);
        translate([3, 7.5, 11]) sphere(d = 1.5);
    }
    color("black") translate([3, 7.5, 11.5]) sphere(d = 0.8);
}

// Fishing net storage at stern
module net_storage() {
    color("tan") {
        // Net bundle
        translate([82, 0, 10.5]) sphere(d = 4);

        // Binding ropes
        for (i = [-1, 1]) {
            translate([82, i*1.5, 10.5]) rotate([90, 0, 0]) 
                cylinder(h = 3, d = 0.3);
        }
    }
}

// Main assembly
union() {
    hull();
    sternpost();
    benches();
    oars();
    protective_eye();
    net_storage();

    // Waterline marking (painted)
    color("saddlebrown") 
    translate([45, 0, 2]) cube([90, 20, 0.5]);
}

// Hoplite Shield (Aspis) - Historical Greek Artifact
// Medium complexity realistic model for 3D printing

$fn = 32;

// Shield parameters
shield_diameter = 80;
shield_thickness = 6;
boss_diameter = 30;
boss_height = 12;
rim_width = 4;

// Colors
bronze_color = [0.72, 0.53, 0.04];
leather_color = [0.45, 0.32, 0.18];

// Main shield body
module shield_body() {
    difference() {
        // Main circular shield
        color(bronze_color)
        cylinder(h=shield_thickness, d=shield_diameter, center=true);

        // Central boss hole
        translate([0, 0, shield_thickness/2])
        cylinder(h=shield_thickness+1, d=boss_diameter+8, center=true);
    }
}

// Central boss (umbo)
module shield_boss() {
    difference() {
        // Main boss dome
        color(bronze_color)
        translate([0, 0, shield_thickness/2 + boss_height/2])
        scale([1, 1, 0.8])
        sphere(d=boss_diameter);

        // Inner cutout for hand grip
        translate([0, 0, shield_thickness/2 + boss_height/2])
        sphere(d=boss_diameter-12);
    }
}

// Decorative bronze rim
module shield_rim() {
    color(bronze_color)
    difference() {
        cylinder(h=shield_thickness+2, d=shield_diameter, center=true);
        cylinder(h=shield_thickness+3, d=shield_diameter-rim_width*2, center=true);
    }
}

// Leather hand grip on back
module hand_grip() {
    color(leather_color)
    translate([0, -15, -shield_thickness/2])
    rotate([90, 0, 0])
    cylinder(h=25, d=8, center=true);
}

// Decorative bronze studs around boss
module decorative_studs() {
    color(bronze_color)
    for(i = [0:7]) {
        rotate([0, 0, i * 45])
        translate([boss_diameter/2 + 8, 0, shield_thickness/2])
        cylinder(h=2, d=4, center=true);
    }
}

// Back reinforcement strips
module back_strips() {
    color(leather_color)
    for(i = [0:1]) {
        rotate([0, 0, i * 90])
        translate([0, 0, -shield_thickness/2])
        cube([shield_diameter-10, 6, 3], center=true);
    }
}

// Assembly
union() {
    // Main shield components
    shield_body();
    shield_rim();
    shield_boss();

    // Decorative elements
    decorative_studs();

    // Back side components
    hand_grip();
    back_strips();
}

// Ancient Greek Bronze Muscle Cuirass (Medium Complexity)
// 5th Century BC Hoplite Armor - 3D Printable Model

$fa = 1;
$fs = 0.5;

// Main color - antiqued bronze
color([0.72, 0.52, 0.35]) {
    // Main torso section
    difference() {
        // Front torso plate
        union() {
            // Chest muscle definition
            translate([0, 0, 25]) {
                scale([1, 1.2, 0.8]) {
                    sphere(25);
                }
            }

            // Abdomen section
            translate([0, 0, 5]) {
                cube([50, 60, 20], center = true);
            }

            // Lower edge flare
            translate([0, 0, -10]) {
                cylinder(h = 5, r1 = 30, r2 = 35);
            }
        }

        // Neck hole
        translate([0, 20, 25]) {
            cylinder(h = 40, r = 8);
        }

        // Arm holes
        translate([25, 0, 15]) {
            rotate([0, 90, 0]) {
                cylinder(h = 30, r = 10);
            }
        }
        mirror([1, 0, 0]) {
            translate([25, 0, 15]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 30, r = 10);
                }
            }
        }

        // Sculpted muscle grooves
        for (i = [0:1]) {
            translate([i == 0 ? 8 : -8, 5, 30]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 15, r = 3);
                }
            }
        }

        // Abdominal grooves
        for (i = [0:2]) {
            translate([0, -10 + i * 10, 10]) {
                cube([40, 1, 15], center = true);
            }
        }
    }

    // Decorative pectoral ridge
    translate([0, 15, 35]) {
        scale([1, 0.3, 0.2]) {
            sphere(20);
        }
    }

    // Side hinges (simplified)
    module hinge(x) {
        translate([x, -25, 15]) {
            union() {
                cylinder(h = 5, r = 4);
                translate([0, 0, 5]) {
                    sphere(4);
                }
            }
        }
    }

    hinge(30);
    hinge(-30);

    // Decorative border pattern
    color([0.65, 0.45, 0.28]) {
        for (i = [0:35:360]) {
            rotate([0, 0, i]) {
                translate([0, -30, 0]) {
                    sphere(2);
                }
            }
        }
    }

    // Back plate (simplified)
    translate([0, 0, -45]) {
        difference() {
            scale([0.9, 1, 0.6]) {
                sphere(30);
            }

            // Back neck hole
            translate([0, 20, 10]) {
                cylinder(h = 20, r = 8);
            }
        }
    }

    // Lower tassets (hip guards)
    module tasset(x) {
        translate([x, -40, -15]) {
            difference() {
                cube([15, 20, 3]);
                translate([0, -1, -1]) {
                    cube([17, 2, 5]);
                }
            }
        }
    }

    tasset(15);
    tasset(-15);

    // Central decorative boss
    color([0.55, 0.35, 0.18]) {
        translate([0, 5, 30]) {
            cylinder(h = 3, r = 6);
            translate([0, 0, 3]) {
                sphere(6);
            }
        }
    }
}

// Historical Bronze Greaves (Ancient Greek)
// Medium complexity realistic model with decorative elements

$fn = 64;

// Main color - aged bronze
color([0.65, 0.45, 0.25]) difference() {
    union() {
        // Main shin guard body
        difference() {
            // Outer curved shell
            translate([0, -15, 0])
                scale([1, 0.6, 1.2])
                rotate([0, 90, 0])
                cylinder(h = 60, r1 = 25, r2 = 22);

            // Inner cavity for leg
            translate([0, -15, 5])
                scale([0.9, 0.5, 1.1])
                rotate([0, 90, 0])
                cylinder(h = 55, r1 = 23, r2 = 20);
        }

        // Knee cap protection
