// Falcata - Iberian Iron Age sword (4th-1st century BC)
// Realistic style, medium complexity, 3D printable

// Global settings
$fn = 50;
blade_length = 75;
blade_width = 25;
blade_thickness = 3;
handle_length = 60;
handle_thickness = 12;

// Main assembly
module falcata() {
    color("silver") {
        union() {
            // Curved single-edged blade
            blade();

            // Reinforced spine with decorative ridge
            blade_spine();

            // Hooked grip with horse-head pommel
            handle();

            // Decorative inlay on blade
            blade_inlay();
        }
    }
}

// Curved blade with single cutting edge
module blade() {
    // Main blade body - curved forward
    translate([0, 0, blade_thickness/2])
    rotate([0, 0, -15])
    linear_extrude(height=blade_thickness, convexity=10)
    polygon(points=[
        [0, 0],                    // Hilt junction
        [blade_length * 0.3, blade_width * 0.8],   // Mid curve
        [blade_length * 0.7, blade_width * 0.9],   // Widest point
        [blade_length, blade_width * 0.3],         // Tip
        [blade_length * 0.9, 0],                   // Back edge to tip
        [blade_length * 0.5, -blade_width * 0.2],  // Concave back
        [blade_length * 0.2, -blade_width * 0.1],  // Back to hilt
        [0, 0]
    ]);

    // Blade bevel for cutting edge
    translate([0, 0, blade_thickness/2])
    rotate([0, 0, -15])
    linear_extrude(height=blade_thickness * 0.5, convexity=10)
    offset(r=-1)
    polygon(points=[
        [blade_length * 0.3, blade_width * 0.8],
        [blade_length * 0.7, blade_width * 0.9],
        [blade_length, blade_width * 0.3],
        [blade_length * 0.95, blade_width * 0.25]
    ]);
}

// Reinforced spine ridge
module blade_spine() {
    color("gray") {
        translate([blade_length * 0.3, -blade_width * 0.1, blade_thickness])
        rotate([0, 10, -15])
        scale([1, 0.3, 0.3])
        cylinder(h=blade_length * 0.6, r1=3, r2=1);

        // Decorative spine notches
        for(i = [1:3]) {
            translate([blade_length * (0.4 + i * 0.15), -blade_width * 0.1, blade_thickness + 1])
            rotate([0, 10, -15])
            cube([2, 4, 1], center=true);
        }
    }
}

// Horse-head pommel handle
module handle() {
    // Main grip
    translate([-handle_length * 0.3, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=handle_length, r=handle_thickness/2, center=true);

    // Hooked grip extension
    translate([-handle_length * 0.7, handle_thickness * 0.8, 0])
    rotate([0, 90, 30])
    scale([1, 1, 0.8])
    cylinder(h=handle_length * 0.4, r=handle_thickness/2 * 0.8);

    // Horse head pommel detail
    color("bronze") {
        translate([-handle_length * 0.85, handle_thickness * 1.2, 0])
        scale([1.2, 1.5, 1])
        sphere(r=handle_thickness/2 * 0.8);

        // Horse ears
        translate([-handle_length * 0.85, handle_thickness * 1.6, handle_thickness/2 * 0.5])
        rotate([30, 0, 0])
        cylinder(h=3, r=1);

        translate([-handle_length * 0.85, handle_thickness * 1.6, -handle_thickness/2 * 0.5])
        rotate([-30, 0, 0])
        cylinder(h=3, r=1);
    }

    // Grip wrapping rings
    color("bronze") {
        for(i = [0.2:0.2:0.8]) {
            translate([-handle_length * i, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=2, r=handle_thickness/2 + 1);
        }
    }
}

// Decorative inlay pattern
module blade_inlay() {
    color("bronze") {
        // Central inlay line
        translate([blade_length * 0.5, blade_width * 0.3, blade_thickness + 0.1])
        rotate([0, 0, -15])
        linear_extrude(height=0.5)
        polygon(points=[
            [0, 0],
            [blade_length * 0.3, 0],
            [blade_length * 0.25, 1],
            [-blade_length * 0.05, 1]
        ]);

        // Decorative circles
        for(pos = [[blade_length * 0.4, blade_width * 0.5], 
                   [blade_length * 0.6, blade_width * 0.6]]) {
            translate([pos[0], pos[1], blade_thickness + 0.1])
            cylinder(h=0.5, r=2);
        }
    }
}

