// Lamellar armor plate - Byzantine style 6th-10th century
// Individual lamellae were laced together to form flexible armor
// This represents a single lamellar plate as would be used in historical armor

// Overall dimensions for 3D printing
plate_width = 20;
plate_height = 40;
plate_thickness = 2;
hole_diameter = 2;
hole_offset = 4;

// Historical decorative elements
decoration_width = 14;
decoration_height = 6;
decoration_depth = 0.5;

// Color definitions
bronze_color = [150/255, 120/255, 90/255];
leather_color = [101/255, 67/255, 33/255];

module lamellar_plate() {
    // Main plate body with rounded top
    union() {
        // Base rectangular plate
        difference() {
            cube([plate_width, plate_height, plate_thickness], center=true);

            // Rounded top edge
            translate([0, plate_height/2 - plate_width/2, plate_thickness/2])
                rotate([90, 0, 0])
                    cylinder(h=plate_width, r1=plate_width/2, r2=plate_width/2, center=true);
        }

        // Historical decorative ridge
        translate([0, plate_height/4, plate_thickness/2 + decoration_depth/2])
            cube([decoration_width, decoration_height, decoration_depth], center=true);

        // Secondary decorative line
        translate([0, -plate_height/4, plate_thickness/2 + decoration_depth/2])
            cube([decoration_width*0.8, decoration_height*0.6, decoration_depth], center=true);
    }

    // Lacing holes - historically accurate spacing
    for (y = [-plate_height/2 + hole_offset*2, -plate_height/2 + hole_offset*4]) {
        for (x = [-plate_width/2 + hole_offset, plate_width/2 - hole_offset]) {
            translate([x, y, plate_thickness/2])
                rotate([0, 180, 0])
                    cylinder(h=plate_thickness+1, d=hole_diameter, center=true);
        }
    }

    // Additional side holes for horizontal lacing
    for (y = [-plate_height/2 + hole_offset*3, plate_height/2 - hole_offset*3]) {
        translate([plate_width/2 + 0.1, y, 0])
            rotate([0, 90, 0])
                cylinder(h=plate_thickness+0.2, d=hole_diameter, center=true);
    }
}

// Create a small section of lamellar armor showing how plates overlap
module lamellar_section() {
    color(bronze_color) {
        // Base plate
        lamellar_plate();

        // Second plate overlapping (historical overlap was 1/3 to 1/2)
        translate([0, plate_height/3, plate_thickness*0.8])
            rotate([0, 0, 5]) // Slight angle for realistic overlap
            lamellar_plate();

        // Third plate
        translate([0, -plate_height/3, plate_thickness*1.6])
            rotate([0, 0, -3])
            lamellar_plate();
    }

    // Lacing cords representation
    color(leather_color) {
        for (x = [-plate_width/2 + hole_offset, plate_width/2 - hole_offset]) {
            for (z = [0, plate_thickness*0.8, plate_thickness*1.6]) {
                translate([x, -plate_height/2 + hole_offset*2, z])
                    rotate([90, 0, 0])
                        cylinder(h=plate_height, d=1.5, center=false);
            }
        }
    }
}

// Render the lamellar section
lamellar_section();

// Brigandine Vest
// A late 14th-century torso defense made from small overlapping iron plates riveted inside a fabric or leather shell

$fa = 2;
