        translate([0, -h/3, t/2+0.1])
        cylinder(h=t+0.2, r=0.8, $fn=6);
    }
}

module brigandine_row(count=6, offset=0) {
    // Row of overlapping plates
    for(i=[0:count-1]) {
        translate([i*10 - (count-1)*5 + offset, 0, 0])
        rotate([0, -5, 0])
        plate();
    }
