iron = [0.4, 0.4, 0.45];
dark_iron = [0.3, 0.3, 0.35];

// Ring pattern module - creates individual chainmail rings
module chain_ring(diameter=2.5, thickness=0.8, height=1.2) {
    difference() {
        cylinder(h=height, d=diameter, $fn=8);
        translate([0, 0, -0.1])
            cylinder(h=height+0.2, d=diameter-thickness, $fn=8);
    }
}

// Create a row of chainmail rings with alternating pattern
module chain_row(length, y_offset=0) {
    for(i=[0:ceil(length/2.5)-1]) {
        if(i%2==0) {
            translate([i*2.5, y_offset, 0])
                chain_ring();
        } else {
            translate([i*2.5, y_offset+1.25, 0])
                chain_ring();
        }
    }
}

// Create chainmail mesh section
module chain_section(width, height, start_x=0, start_y=0) {
    for(j=[0:ceil(height/2.5)-1]) {
        if(j%2==0) {
            chain_row(width, start_y+j*2.165);
        } else {
            chain_row(width-1.25, start_y+j*2.165);
        }
    }
}

// Main hauberk body
module hauberk_body() {
    color(iron) {
        // Front torso section
        chain_section(40, 50, 0, 0);

        // Back torso section (mirrored)
        translate([0, 60, 0])
            mirror([0, 1, 0])
                chain_section(40, 50, 0, 0);

        // Sleeves - left
        translate([-20, 20, 0])
            rotate([0, 0, 45])
                chain_section(25, 30, 0, 0);

        // Sleeves - right
