module mail_pattern(width, height, depth) {
    for (i = [0:ceil(width/ring_spacing)])
        for (j = [0:ceil(height/ring_spacing)]) {
            x = i * ring_spacing - width/2;
            y = j * ring_spacing - height/2;
            offset = (j % 2) * ring_spacing/2;
            mail_ring(x + offset, y, depth);
        }
}

module torso_base() {
    color("silver") difference() {
        // Main torso
        translate([0, 0, torso_h/2])
        cube([torso_w, torso_d, torso_h], center = true);

