        translate([0, torso_d/2 - 2, torso_h - neck_r])
        rotate([90, 0, 0])
        cylinder(h = torso_d + 4, r = neck_r, $fn = 16);

        // Arm holes
        translate([torso_w/2 - 1, 0, torso_h - arm_hole])
        rotate([0, 90, 0])
        cylinder(h = 4, r = arm_hole - 1, $fn = 16);
        mirror([1, 0, 0])
        translate([torso_w/2 - 1, 0, torso_h - arm_hole])
        rotate([0, 90, 0])
        cylinder(h = 4, r = arm_hole - 1, $fn = 16);

        // Bottom taper
        translate([0, 0, -1])
        cube([torso_w + 2, torso_d + 2, 2], center = true);
    }
}

