        // Hem border
        translate([0, 0, border_th/2])
        cube([torso_w + 2, torso_d + 1, border_th], center = true);

        // Side reinforcements
        translate([torso_w/2 - border_w/2, 0, torso_h/2])
        cube([border_w, torso_d + 0.5, torso_h], center = true);
        mirror([1, 0, 0])
        translate([torso_w/2 - border_w/2, 0, torso_h/2])
        cube([border_w, torso_d + 0.5, torso_h], center = true);
    }
}

module shoulder_straps() {
