// Spangenhelm - 9th-11th century Viking helmet
// Medium complexity realistic model
// Dimensions: ~75mm total height, ~60mm width

// Main colors
$fn = 64;

// Helmet proportions
helmet_diameter = 55; // Inner diameter for head
helmet_height = 45;
band_width = 5;
plate_thickness = 2;

// Create the complete helmet
module spangenhelm() {
    color("silver") {
        // Main dome segments (4 plates)
        difference() {
            union() {
                // Front plate
                translate([0, 0, helmet_height/2])
                    scale([1, 0.9, 1])
                    sphere(d=helmet_diameter);

                // Back plate
                translate([0, 0, helmet_height/2])
                    scale([1, 0.9, 0.8])
                    rotate([180, 0, 0])
                    sphere(d=helmet_diameter);

                // Left plate
                translate([0, 0, helmet_height/2])
                    scale([0.9, 1, 1])
                    rotate([0, 90, 0])
                    sphere(d=helmet_diameter);

                // Right plate
                translate([0, 0, helmet_height/2])
                    scale([0.9, 1, 1])
                    rotate([0, -90, 0])
                    sphere(d=helmet_diameter);
            }

            // Cut interior hollow
            translate([0, 0, helmet_height/2 - 5])
                sphere(d=helmet_diameter - 6);
        }

        // Spangen bands (framework strips)
        // Horizontal band
        translate([0, 0, helmet_height/2])
            rotate_extrude()
                translate([helmet_diameter/2 - band_width/2, 0, 0])
                    square([band_width, plate_thickness]);

        // Vertical bands (4 pieces)
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
                translate([helmet_diameter/2 - band_width/2, 0, helmet_height/2])
                    rotate([90, 0, 0])
                        cylinder(h=helmet_diameter/2, r=band_width/2, $fn=4);
        }

        // Nasal guard
        translate([0, helmet_diameter/2 - 8, helmet_height/2 - 5])
            rotate([90, 0, 0])
                linear_extrude(height=band_width)
                    polygon(points=[
                        [0, 0],
                        [6, -15],
                        [3, -25],
                        [-3, -25],
                        [-6, -15],
                        [0, 0]
                    ]);

        // Decorative rivets along bands
        color("goldenrod") {
            // Rivets on horizontal band
            for(i = [0:7]) {
                rotate([0, 0, i * 45])
                    translate([helmet_diameter/2 - band_width/2, 0, helmet_height/2 + plate_thickness])
                        sphere(d=1.5);
            }

            // Rivets on vertical bands
            for(i = [0:3]) {
                for(j = [1:3]) {
                    rotate([0, 0, i * 90])
                        translate([helmet_diameter/2 - band_width/2 - j*5, 0, helmet_height/2 + plate_thickness])
                            sphere(d=1.5);
                }
            }
        }

        // Chain mail aventail attachment points
        color("darkgray") {
            for(i = [0:11]) {
                rotate([0, 0, i * 30])
                    translate([helmet_diameter/2 - 2, 0, helmet_height/2 - helmet_height/4])
                        cylinder(h=3, r=1.5);
            }
        }

        // Decorative bronze brow band
        color("bronze") {
            translate([0, 0, helmet_height/2 + 8])
                rotate_extrude()
                    translate([helmet_diameter/2 + 2, 0, 0])
                        square([1.5, 3]);
        }
    }
}

