// Medieval Chausses - 14th Century Mail Leg Armor
// Scale: 1:6 (approximately 80mm height)

// Colors
chain_mail_color = [0.4, 0.4, 0.45];
leather_color = [0.3, 0.2, 0.1];
cloth_color = [0.5, 0.4, 0.3];

// Main parameters
leg_length = 80;
leg_width = 18;
wall_thickness = 1.5;

module mail_texture() {
    for (i = [0:3]) {
        for (j = [0:8]) {
            translate([i * 2.5, j * 2.5, 0])
                sphere(d = 1.8, $fn = 8);
        }
    }
}

module chain_mail_surface(width, height) {
    color(chain_mail_color) {
        for (x = [-width/2:2:width/2]) {
            for (y = [0:2:height]) {
                translate([x, y, 0])
                    mail_texture();
            }
        }
    }
}

module foot_cover() {
    color(leather_color) {
        // Sole
        translate([0, 0, -leg_width/2])
            cube([leg_width * 0.8, leg_width * 1.2, wall_thickness], center = true);

        // Toe cap
        translate([0, leg_width * 0.6, 0])
            rotate([90, 0, 0])
                cylinder(h = leg_width * 0.4, r1 = leg_width * 0.4, r2 = leg_width * 0.3, $fn = 16);

        // Heel reinforcement
        translate([0, -leg_width * 0.5, 0])
            rotate([90, 0, 0])
                cylinder(h = leg_width * 0.3, r1 = leg_width * 0.35, r2 = leg_width * 0.3, $fn = 16);
    }
}

module knee_pieces() {
    color(leather_color) {
        // Upper knee plate
        translate([0, leg_length * 0.75, leg_width/2])
            rotate([90, 0, 0])
                cylinder(h = wall_thickness * 2, r1 = leg_width * 0.7, r2 = leg_width * 0.6, $fn = 16);

        // Decorative rivets
        for (angle = [0:60:300]) {
            rotate([0, 0, angle])
                translate([leg_width * 0.55, leg_length * 0.75, leg_width/2 + wall_thickness])
                    sphere(d = 1.2, $fn = 8);
        }
    }
}

module strap() {
    color(cloth_color) {
        // Upper strap
        translate([0, leg_length - 5, leg_width/2 + 2])
            cube([leg_width * 1.5, 3, wall_thickness], center = true);

        // Buckle
        translate([leg_width * 0.8, leg_length - 5, leg_width/2 + 3])
            difference() {
                cube([4, 2, 1], center = true);
                translate([0, 0, -0.5])
                    cube([3, 1, 2], center = true);
            }
    }
}

module leg_segment(start_y, end_y, width) {
    hull() {
        translate([0, start_y, 0])
            sphere(d = width, $fn = 16);
        translate([0, end_y, 0])
            sphere(d = width * 0.9, $fn = 16);
    }
}

module chausses_leg() {
    // Main leg structure
    difference() {
        // Outer shell
        union() {
            for (i = [0:5:leg_length]) {
                leg_segment(i, i + 5, leg_width - i * 0.1);
            }
        }

        // Hollow interior
        translate([0, 0, 0])
            scale([0.7, 1, 0.7])
                cylinder(h = leg_length + 5, r = leg_width/2 - wall_thickness, center = false, $fn = 16);
    }

    // Add chain mail texture
    chain_mail_surface(leg_width, leg_length);

    // Add foot
    translate([0, 0, -leg_width/2])
        foot_cover();

    // Add knee protection
    knee_pieces();

    // Add securing strap
    strap();
}

// Main assembly
translate([0, 0, leg_width/2])
    rotate([0, 90, 0])
        chausses_leg();

$fn = 64;

// Overall scale factor for 3D printing (fits ~90 mm)
scale_factor = 0.4;
color("silver") scale([scale_factor, scale_factor, scale_factor]) 
union() {

    // Main toe plates (symmetrical)
    difference() {
        union() {
            for (i = [0:1:2]) {
                translate([i*20, 0, 0]) 
                cube([18, 36, 2], center=true);
            }
        }
        // Remove inner toe area (hollow shell)
        translate([20, 0, 0]) 
        cube([54, 32, 2.5], center=true);
    }

    // Side plates for toe
    for (side = [-1, 1]) {
        translate([0, side*18, 0]) 
        rotate([90, 0, 0]) 
        cylinder(h=2, r=5);
    }

    // Central metatarsal plate (slightly larger)
    translate([60, 0, 0]) 
    cube([22, 40, 2.5], center=true);

    // Heel plate (rounded)
    translate([85, 0, 0]) 
    difference() {
        scale([1, 0.7, 1]) 
        sphere(r=20);
        translate([0, 0, -20]) 
        cube([40, 40, 20], center=true);
    }

    // Ankle plate (articulated)
    translate([100, 0, 5]) 
    cube([15, 30, 2], center=true);

    // Decorative rivets
    for (row = [0:1:1]) {
        for (col = [0:1:3]) {
            translate([col*20 + 10, (row - 0.5)*30, 1]) 
            cylinder(h=2, r=1.5, $fn=16);
        }
    }

    // Historical engravings (simplified as grooves)
    for (i = [0:1:2]) {
        translate([i*20 + 10, 0, 1.1]) 
        cube([1, 25, 0.5], center=true);
    }

    // Side hinges (simplified)
    for (side = [-1, 1]) {
        translate([40, side*20, 0]) 
        rotate([90, 0, 0]) 
        cylinder(h=3, r=2);
    }

    // Toe cap reinforcement
    translate([-10, 0, 0]) 
    difference() {
        scale([1, 1, 0.5]) 
        sphere(r=18);
        translate([0, 0, -9]) 
        cube([36, 36, 9], center=true);
    }

    // Leather straps (simulated as ridges)
    for (strap = [0:1:1]) {
        translate([strap*40 + 30, 0, 2.1]) 
        cube([5, 38, 0.5], center=true);
    }
}

// Pauldrons - 15th century historical shoulder armor
// Dimensions: 80mm x 60mm x 25mm

color("silver")
union() {
    // Main shoulder plate
    translate([0, 0, 10])
    difference() {
        scale([1, 1.2, 0.3])
        sphere(30);

        // Hollow interior for printing
        translate([0, 0, -5])
        scale([0.9, 1.1, 0.2])
        sphere(30);
    }

    // Upper arm lames
    for (i = [0:2]) {
        translate([0, -25 - i*8, 12 - i*3])
        rotate([0, -20 + i*5, 0])
        difference() {
            scale([0.8 - i*0.1, 1, 0.15])
            sphere(20);

            translate([0, 0, -5])
            scale([0.7 - i*0.1, 0.9, 0.1])
            sphere(20);
        }
    }

    // Decorative rivets
    module rivet(x, y, z) {
        translate([x, y, z])
        cylinder(h=2, r=1.5, $fn=8);
    }

    for (angle = [0:60:300]) {
        rivet(cos(angle)*25, sin(angle)*25, 20);
    }

    // Articulated plate joints
    module plate_joint(y_pos) {
        translate([0, y_pos, 10])
        difference() {
            cube([15, 3, 5], center=true);
            translate([0, 0, 2.5])
            cylinder(h=6, r=2, center=true);
        }
    }

    plate_joint(-20);
    plate_joint(-28);

    // Heraldic emblem (fleur-de-lis)
    translate([25, 0, 21])
    linear_extrude(height=1.5)
    polygon(points=[
        [0, 0],
        [-3, 5],
        [-1.5, 5],
        [-1.5, 8],
        [1.5, 8],
        [1.5, 5],
        [3, 5]
    ]);

    // Leather strap attachment points
    module strap_mount(x, y) {
        translate([x, y, 15])
        difference() {
            cylinder(h=8, r=3, $fn=16);
            cylinder(h=10, r=1.5, $fn=16);
        }
    }

    strap_mount(-30, 25);
    strap_mount(30, 25);

    // Decorative edge ridges
    for (angle = [0:10:360]) {
        translate([cos(angle)*30, sin(angle)*36, 10])
        rotate([90, 0, 0])
        cylinder(h=2, r1=1, r2=0.5);
    }

    // Lower shoulder extension
    translate([0, 10, 8])
    rotate([90, 0, 0])
    difference() {
        cylinder(h=15, r1=25, r2=20, $fn=32);
        cylinder(h=16, r1=22, r2=17, $fn=32);
    }
}

// Historical Tassets - Renaissance Plate Armor (c. 1550)
// Each tasset is roughly 70mm high, designed to hang from a cuirass

$fn = 32;

// Main dimensions
tasset_width = 30;
tasset_height = 70;
tasset_thickness = 2.5;
rivet_diameter = 1.5;
rivet_height = 1;

// Create a single tasset plate
module tasset_plate() {
    color("silver") {
        // Main plate with slight curvature
        difference() {
            // Curved main plate
            translate([0, 0, tasset_height/2])
            scale([1, 1.2, 1])
            rotate([0, 90, 0])
            cylinder(h = tasset_width, r1 = 40, r2 = 38, center = true);

            // Remove back half for proper curve
            translate([0, -tasset_width/2, tasset_height/2])
            cube([100, tasset_width, tasset_height], center = false);
        }
    }
}

// Decorative ridge along the center
module central_ridge() {
    color("silver") 
    translate([0, 1.5, tasset_height/2])
    scale([0.1, 1, 1.1])
    rotate([0, 90, 0])
    cylinder(h = tasset_width * 0.8, r1 = 40, r2 = 38, center = true);
}

// Rivets for attaching straps
module rivets() {
    color("dimgray")
    for(i = [0:1]) {
        // Top rivets
        translate([0, 2, 60 + i * 5])
        cylinder(h = rivet_height, d = rivet_diameter, center = true);

        // Bottom rivets
        translate([0, 2, 10 + i * 5])
        cylinder(h = rivet_height, d = rivet_diameter, center = true);
    }
}

// Decorative etching pattern
module etching_pattern() {
    color("dimgray") {
        // Top decorative band
        translate([0, 2.6, 55])
        cube([tasset_width * 0.8, 0.2, 8]);

        // Bottom decorative band
        translate([0, 2.6, 15])
        cube([tasset_width * 0.8, 0.2, 8]);

        // Vertical lines
        for(x = [-10:5:10]) {
            translate([x, 2.6, 35])
            cube([0.5, 0.2, 30]);
        }
    }
}

// Articulated lames (overlapping plates)
module lames() {
    color("silver") {
        for(i = [1:4]) {
            translate([0, -i * 0.5, 40 - i * 8])
            scale([1 - i * 0.05, 1, 0.8])
            rotate([0, 90, 0])
            cylinder(h = tasset_width * 0.9, r1 = 40, r2 = 38, center = true);
        }
    }
}

// Complete left tasset
module left_tasset() {
    union() {
        tasset_plate();
        central_ridge();
        rivets();
        etching_pattern();
        lames();
    }
}

// Complete right tasset (mirrored)
module right_tasset() {
    mirror([1, 0, 0])
    left_tasset();
}

// Assembly - pair of tassets
left_tasset();
translate([tasset_width + 5, 0, 0])
right_tasset();

// Kettlehat - Medieval European steel helmet
// Designed for 3D printing with 1.2mm walls and no overhangs

$fn = 64;

// Main parameters
hat_diameter = 80;
hat_height = 35;
brim_width = 15;
brim_thickness = 2;
wall_thickness = 1.2;
rivet_diameter = 2;
rivet_height = 1;

module kettlehat() {
    // Main bowl - conical shape typical of kettlehat
    difference() {
        // Outer cone
        cylinder(h = hat_height, r1 = hat_diameter/2, r2 = hat_diameter/2 - 8);

        // Inner cone - hollow it out
        translate([0, 0, -0.1])
            cylinder(h = hat_height + 0.2, 
                   r1 = hat_diameter/2 - wall_thickness, 
                   r2 = hat_diameter/2 - 8 - wall_thickness);
    }

    // Wide circular brim - key feature of kettlehat
    color("silver")
    difference() {
        // Brim base
        translate([0, 0, hat_height - brim_thickness/2])
            cylinder(h = brim_thickness, r1 = hat_diameter/2 + brim_width, r2 = hat_diameter/2 + brim_width);

        // Cut hole for head
        translate([0, 0, hat_height - brim_thickness - 0.1])
            cylinder(h = brim_thickness + 0.2, r1 = hat_diameter/2 - wall_thickness, r2 = hat_diameter/2 - wall_thickness);
    }

    // Reinforcement band around base of cone
    color("gray")
    translate([0, 0, hat_height - brim_thickness - 2])
        difference() {
            cylinder(h = 2, r1 = hat_diameter/2 + 1, r2 = hat_diameter/2 + 1);
            cylinder(h = 2.2, r1 = hat_diameter/2 - 1, r2 = hat_diameter/2 - 1);
        }

    // Decorative rivets around brim - 8 rivets total
    for (i = [0:7]) {
        angle = i * 45;
        translate([
            cos(angle) * (hat_diameter/2 + brim_width/2),
            sin(angle) * (hat_diameter/2 + brim_width/2),
            hat_height
        ])
            cylinder(h = rivet_height, d = rivet_diameter);
    }

    // Central raised boss - typical decorative element
    color("silver")
    translate([0, 0, -1])
        difference() {
            cylinder(h = 3, r1 = 6, r2 = 4);
            cylinder(h = 3.2, r1 = 4, r2 = 2);
        }

    // Ventilation holes - 4 holes around crown
    for (i = [0:3]) {
        angle = i * 90 + 22.5;
        translate([
            cos(angle) * (hat_diameter/4),
            sin(angle) * (hat_diameter/4),
            hat_height - 5
        ])
            rotate([90, 0, 0])
                cylinder(h = 10, d = 3);
    }

    // Leather suspension ring attachment points - 4 points
    for (i = [0:3]) {
        angle = i * 90 + 45;
        translate([
            cos(angle) * (hat_diameter/2 - 6),
            sin(angle) * (hat_diameter/2 - 6),
            hat_height - 10
        ])
            difference() {
                cube([4, 4, 2], center = true);
                cube([3, 3, 2.2], center = true);
            }
    }
}

// Render the kettlehat
kettlehat();

// Angon - Early Medieval Frankish throwing spear (6th-7th century)
// Based on archaeological finds from Merovingian graves

$fn = 64;

// Main colors
color("silver") difference() {
    union() {
        // Spearhead
        angon_head();

        // Socket
        angon_socket();

        // Decorative inlay bands
        inlay_bands();
    }

    // Rivet hole through socket
    translate([0, 0, 5]) rotate([90, 0, 0]) 
        cylinder(h = 15, d = 3, center = true);
}

module angon_head() {
    // Spear blade - leaf-shaped with central ridge
    translate([0, 0, 60]) rotate([0, 0, 0]) {
        // Main blade body
        linear_extrude(height = 2, center = true) {
            polygon(points = [[0, 30], [8, 28], [12, 20], [14, 10], [15, 0], 
                             [14, -10], [12, -20], [8, -28], [0, -30], 
                             [-8, -28], [-12, -20], [-14, -10], [-15, 0], 
                             [-14, 10], [-12, 20], [-8, 28]]);
        }

        // Central ridge on both sides
        translate([0, 0, 1]) rotate([90, 0, 0])
            cylinder(h = 60, d1 = 3, d2 = 1);
        translate([0, 0, -1]) rotate([90, 0, 0])
            cylinder(h = 60, d1 = 3, d2 = 1);
    }
}

module angon_socket() {
    // Hollow socket tube
    difference() {
        // Outer socket
        translate([0, 0, 15]) rotate([90, 0, 0])
            cylinder(h = 30, d1 = 12, d2 = 10);

        // Inner hollow
        translate([0, 0, 15]) rotate([90, 0, 0])
            cylinder(h = 32, d = 8);
    }
}

module inlay_bands() {
    // Decorative copper-alloy inlays typical of Merovingian craftsmanship

    // First band near socket
    color("bronze") translate([0, 0, 30]) rotate([90, 0, 0])
        cylinder(h = 2, d = 14);

    // Second band further up
    color("bronze") translate([0, 0, 45]) rotate([90, 0, 0])
        cylinder(h = 2, d = 13);

    // Dot pattern on socket
    for (i = [0:5]) {
        color("bronze") rotate([0, 0, i * 60]) 
            translate([6, 0, 15]) sphere(d = 1.5);
    }

    // Additional dot pattern on blade
    for (i = [0:3]) {
        color("bronze") rotate([0, 0, i * 90]) 
            translate([10, 0, 50]) sphere(d = 1);
    }
}

// Francisca throwing axe - 6th-8th century Frankish weapon
// Historically accurate proportions and features

// Overall dimensions for 3D printing
$fn = 32;

// Colors for materials
color_wood = [139/255, 90/255, 43/255];
color_iron = [0.3, 0.3, 0.3];
color_bronze = [0.8, 0.5, 0.2];

module handle() {
    // Wooden ash handle with slight taper
    color(color_wood) {
        // Main handle shaft
        for (i = [0:9]) {
            translate([0, 0, i*5]) {
                cylinder(h = 5, d1 = 15-i*0.4, d2 = 15-(i+1)*0.4, center = false);
            }
        }

        // Leather grip rings
        for (i = [1:3]) {
            color([0.1, 0.1, 0.1]) {
                translate([0, 0, i*12]) {
                    cylinder(h = 2, d = 16, center = false);
                }
            }
        }

        // End knob
        sphere(d = 18);
    }
}

module axe_head() {
    color(color_iron) {
        // Main blade body
        difference() {
            // Forged iron blade
            translate([-20, 0, 0]) {
                rotate([0, 90, 0]) {
                    scale([1, 0.3, 1]) {
                        sphere(d = 60);
                    }
                }
            }

            // Hollow socket
            translate([0, 0, -10]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 30, d = 16, center = true);
                }
            }

            // Blade edge bevel
            translate([-35, 0, 0]) {
                rotate([0, 90, 0]) {
                    cube([30, 30, 8], center = true);
                }
            }
        }

        // Socket collar
        color(color_bronze) {
            translate([-5, 0, 0]) {
                rotate([0, 90, 0]) {
                    cylinder(h = 12, d1 = 20, d2 = 22, center = true);
                }
            }
        }

        // Decorative rivets
        for (angle = [0:120:240]) {
            color(color_bronze) {
                rotate([0, angle, 0]) {
                    translate([-8, 0, 10]) {
                        cylinder(h = 3, d = 4, center = true);
                    }
                }
            }
        }

        // Blade fuller
        translate([-15, 0, 0]) {
            rotate([0, 90, 0]) {
                scale([0.8, 0.5, 0.1]) {
                    cylinder(h = 30, d = 50, center = true);
                }
            }
        }
    }
}

module francisca() {
    union() {
        // Assemble components
        translate([0, 0, 0]) {
            handle();
        }

        translate([0, 0, 55]) {
            rotate([0, 0, 45]) {
                axe_head();
            }
        }

        // Hanging loop
        color(color_bronze) {
            translate([-20, 0, 20]) {
                rotate([90, 0, 0]) {
                    difference() {
                        torus(rot = 90, r1 = 3, r2 = 1);
                        translate([3, 0, 0]) {
                            cube([6, 6, 6], center = true);
                        }
                    }
                }
            }
        }
    }
}

// Scale for 3D printing (total length ~80mm)
scale(0.8) {
    francisca();
}

// Seax historical artifact - OpenSCAD model
// Viking age single-edged knife with typical geometric decorations

// Overall dimensions (mm)
blade_length = 70;
blade_width = 18;
handle_length = 30;
handle_width = 12;
total_length = blade_length + handle_length;

// Blade module with characteristic broken-back profile
module blade() {
    difference() {
        // Main blade shape
        translate([0, 0, 2]) {
            hull() {
                // Tip
                translate([blade_length-8, 0, 0])
                    cube([8, blade_width*0.3, 4], center=true);
                // Base
                translate([0, 0, 0])
                    cube([12, blade_width, 4], center=true);
                // Mid section
                translate([blade_length*0.6, 0, 0])
                    cube([8, blade_width*0.5, 4], center=true);
            }
        }

        // Fuller groove
        translate([blade_length*0.3, 0, 3])
            cube([blade_length*0.4, blade_width*0.2, 2], center=true);

        // Edge bevel
        translate([0, -blade_width/2, 1])
            rotate([0, 45, 0])
            cube([blade_length, 3, 3]);
    }

    // Decorative pattern along spine
    for(i = [1:5:blade_length-10]) {
        translate([i, 0, 4.5])
            cube([3, 2, 1], center=true);
    }
}

// Handle with typical Viking wire wrap
module handle() {
    // Core
    color("burlywood") {
        translate([-handle_length, 0, 0])
            cube([handle_length, handle_width, 8], center=true);
    }

    // Wire wrap pattern
    color("goldenrod") {
        for(i = [0:3]) {
            translate([-handle_length*0.8 + i*6, 0, 4])
                rotate([0, 90, 0])
                cylinder(h=handle_width*1.2, r=0.5, center=true);
        }
    }

    // Pommel cap
    color("silver") {
        translate([-handle_length-2, 0, 0])
            difference() {
                sphere(d=handle_width*1.2);
                translate([0, 0, -handle_width*0.6])
                    cube([handle_width*1.2, handle_width*1.2, handle_width*1.2], center=true);
            }
    }
}

// Hilt with guard
module hilt() {
    color("silver") {
        // Guard
        translate([0, 0, 0])
            cube([6, handle_width*1.5, 2], center=true);

        // Hilt collar
        translate([-2, 0, 3])
            cylinder(h=4, r=handle_width/2+1, center=true);
    }
}

// Tang (hidden)
module tang() {
    translate([-handle_length/2, 0, 2])
        cube([handle_length-4, 6, 4], center=true);
}

// Assembly
union() {
    // Blade
    color("lightgray") blade();

    // Hilt assembly
    union() {
        hilt();
        handle();
        tang();
    }
}

// Display stand
color("gray") {
    translate([0, 0, -5])
        cube([total_length+10, 20, 2], center=true);
}

// Bearded Axe - Viking Era Historical Artifact
// Medium complexity realistic design for 3D printing

axe_scale = 1;
handle_length = 80 * axe_scale;
handle_diameter = 8 * axe_scale;
blade_length = 45 * axe_scale;
blade_thickness = 6 * axe_scale;
blade_height = 35 * axe_scale;

// Main assembly
union() {
    // Axe handle
    color([0.4, 0.2, 0.1]) {
        // Handle shaft
        cylinder(h=handle_length, d=handle_diameter, $fn=16);

        // Handle grip texture
        for(i=[0:5:handle_length-15]) {
            translate([0, 0, i]) {
                difference() {
                    cylinder(h=2, d=handle_diameter+1, $fn=16);
                    cylinder(h=3, d=handle_diameter-1, $fn=16);
                }
            }
        }

        // Pommel
        translate([0, 0, handle_length]) {
            sphere(d=handle_diameter+4, $fn=16);
        }
