    translate([0, 0, axe_length/2 - socket_length/2])
        cylinder(h=axe_length - socket_length, d=handle_diameter, $fn=16);

    // Handle texture - leather wrap
    for(i = [0:2:axe_length - socket_length - 10]) {
        translate([0, 0, i + 5])
            rotate([0, 0, i*30])
            difference() {
                cylinder(h=2, d=handle_diameter + 1, $fn=16);
                cylinder(h=2.1, d=handle_diameter, $fn=16);
