// Handaxe - Paleolithic Acheulean style, 3D printable
// Size: ~90mm length, based on typical Acheulean handaxes

module handaxe() {
    color("SaddleBrown") {
        difference() {
            // Main biface body - almond shape
            union() {
                // Upper half
                scale([1, 0.6, 0.15]) 
                    sphere(d=80, $fn=60);

                // Lower half, mirrored
                mirror([0, 1, 0])
                    scale([1, 0.6, 0.15]) 
                        sphere(d=80, $fn=60);
            }

            // Create pointed tip
            translate([35, 0, -10])
                rotate([0, 90, 0])
                    cylinder(h=40, r1=20, r2=0, $fn=30);

            // Create rounded base
            translate([-35, 0, -10])
                rotate([0, 90, 0])
                    cylinder(h=30, r1=20, r2=0, $fn=30);
        }

        // Add flake scars for realism
        for(i = [-30:15:30]) {
            for(j = [-20:10:20]) {
                if(i != 0 && j != 0) {
                    translate([i, j, 2])
                        rotate([0, 0, rand(i*j)*45])
                            scale([1, 1, 0.3])
                                sphere(d=8, $fn=6);
                }
            }
        }

        // Add transverse percussion ripples on both faces
        for(k = [-5:2:5]) {
            translate([k*5, 0, -1])
                rotate([90, 0, 0])
                    cylinder(h=0.5, r=30-k*k, $fn=40);
        }

        // Thicken edges for 3D printing
        difference() {
            // Outer shell
            scale([1.05, 1.05, 1.1])
                handaxe_core();

            // Inner hollow
            scale([0.9, 0.9, 0.8])
                handaxe_core();
        }
    }
}

