module fluke(t) {
    // leaf-shaped side blade
    linear_extrude(height = blade_th, center = true, convexity = 10)
        polygon(points = [
            [0, 0],
            [t*10, 0],
            [t*9, 4],
            [t*7, 5],
            [t*4, 4.5],
            [0, 3]
        ]);
}

module spear_point() {
    // central diamond-section spear head
    hull() {
        translate([0,0,30]) cube([1,1,1], center=true);
        rotate([0,45,0]) cube([blade_th,30,blade_th], center=true);
        rotate([0,-45,0]) cube([blade_th,30,blade_th], center=true);
    }
}

module socket() {
    // hollow conical socket with two rivet holes
    difference() {
        cylinder(h = socket_h, r1 = socket_r, r2 = socket_r*0.9, $fn = 32);
        translate([0,0,-1])
            cylinder(h = socket_h+2, r1 = socket_r - socket_wall,
                     r2 = socket_r*0.9 - socket_wall, $fn = 32);
        // rivet holes
        for (z = [socket_h*0.3, socket_h*0.7])
            translate([socket_r - socket_wall/2, 0, z])
                rotate([0,90,0]) cylinder(h = socket_r*2, r = 1.2, $fn = 12);
    }
}

module corseque_head() {
    union() {
        color("silver")
        // central blade
        translate([0,0,15]) spear_point();

        color("silver")
        // lower flukes
        for (s = [-1, 1])
            translate([0, 0, socket_h + 2])
            rotate([0, 0, s*30]) fluke(s);

        color("silver")
        // socket
        translate([0,0,0]) socket();

        // decorative ring
        color("gold")
        translate([0,0,socket_h+1])
            cylinder(h = 2, r = socket_r + 1, $fn = 32);
    }
}

