// Medieval Tournament Lance (c. 14th century)
// Designed for 3D printing - 80mm total length

$fn = 32;

// Main assembly
module lance() {
    // Colors for realistic appearance
    color("peru") // Wood color
    union() {
        // Main shaft
        shaft();

        // Vamplate (hand guard)
        translate([0, 0, 55]) vamplate();

        // Grip area
        translate([0, 0, 45]) grip();

        // Decorative ferrule at base
        translate([0, 0, 5]) ferrule();

        // Metal tip
        translate([0, 0, 75]) tip();
    }
}

// Wooden shaft
module shaft() {
    // Main shaft body
    cylinder(h = 70, d1 = 6, d2 = 4.5);

    // Reinforcement rings
    for(i = [15:15:60]) {
        translate([0, 0, i])
        difference() {
            cylinder(h = 2, d = 7);
            cylinder(h = 2.2, d = 5);
        }
    }
}

// Hand guard (vamplate) - conical
module vamplate() {
    color("goldenrod")
    difference() {
        // Main cone
        cylinder(h = 8, d1 = 20, d2 = 16);

        // Inner hole
        translate([0, 0, -0.1])
        cylinder(h = 8.2, d = 6);

        // Decorative cutouts
        for(i = [0:45:315]) {
            rotate([0, 0, i])
            translate([8.5, 0, 4])
            rotate([90, 0, 0])
            cylinder(h = 3, d = 3);
        }
    }
}

// Wrapped leather grip
module grip() {
    color("saddlebrown")
    difference() {
        // Base cylinder
        cylinder(h = 12, d = 7.5);

        // Spiral grooves for leather wrapping
        for(i = [0:30:330]) {
            rotate([0, 0, i])
            translate([3.8, 0, 0])
            rotate([0, 90, 0])
            cylinder(h = 12, d = 1.5);
        }
    }
}

// Decorative metal ferrule
module ferrule() {
    color("silver")
    union() {
        // Main ring
        cylinder(h = 4, d = 8);

        // Decorative ridges
        translate([0, 0, 1])
        cylinder(h = 1, d = 9);
        translate([0, 0, 2])
        cylinder(h = 1, d = 9);
    }
}

// Steel lance tip
module tip() {
    color("silver")
    union() {
        // Main pyramid tip
        cylinder(h = 5, d1 = 0, d2 = 5);

        // Reinforcement collar
        translate([0, 0, -2])
        cylinder(h = 2, d = 6);
    }
}

// Base stand for display
module stand() {
    color("dimgray")
    union() {
        // Base plate
        translate([0, 0, -2])
        cube([20, 8, 2], center = true);

        // Support prongs
        translate([-8, 0, 0])
        cube([2, 6, 10], center = true);
        translate([8, 0, 0])
        cube([2, 6, 10], center = true);
    }
}

// Assemble complete model
