    union() {
        // Main wooden shaft
        cylinder(h = shaft_length, d = shaft_diameter, $fn = 16);

        // Wrapping rings every 20mm
        for(i = [0:4]) {
            translate([0, 0, 5 + i * 15])
            color(bronze)
            cylinder(h = 2, d = shaft_diameter + 1.2, $fn = 16);
        }

        // Grip texture - leather wrapping
        translate([0, 0, 35])
        color([0.3, 0.2, 0.1])
        difference() {
            cylinder(h = 15, d = shaft_diameter + 1, $fn = 16);
