    union() {
        // Main leaf-shaped blade
        translate([0, 0, shaft_length])
        linear_extrude(height = tip_length, scale = 0.2)
        polygon(points = [
            [0, 0],
            [2, -6],
            [8, -8],
            [10, -7],
            [10, 7],
            [8, 8],
            [2, 6],
            [0, 0]
        ]);

        // Central ridge
