color_wood = [0.4, 0.26, 0.13];

module bronze_tipped_dart() {
    union() {
        // Main wooden shaft
        color(color_wood)
        translate([0, 0, 5])
        cylinder(h=overall_length - 15, d=shaft_diameter, $fn=16);

        // Bronze socket collar
        color(color_bronze)
        translate([0, 0, 0])
        cylinder(h=8, d=shaft_diameter + 3, $fn=32);

        // Decorative ring engravings on collar
        for(i = [2:2:6]) {
            color(color_bronze)
            translate([0, 0, i])
            difference() {
                cylinder(h=0.5, d=shaft_diameter + 3, $fn=32);
                cylinder(h=0.6, d=shaft_diameter + 2.5, $fn=32);
            }
        }

        // Bronze blade tip
        color(color_bronze)
        union() {
            // Main blade
            translate([0, 0, 8])
            cylinder(h=10, d1=shaft_diameter + 1, d2=2, $fn=6);

            // Side barbs
            for(angle = [0:120:240]) {
                rotate([0, 0, angle])
                translate([shaft_diameter/2 + 1, 0, 8])
                rotate([0, 90, 0])
                cylinder(h=barb_length, d1=1.5, d2=0.5, $fn=8);
            }
        }

        // Shaft grip texture - spiral wrapping
        for(i = [15:5:60]) {
            rotate([0, 0, i * 15])
            translate([shaft_diameter/2 + 0.5, 0, i])
            cube([1, 0.5, 4], center=true);
        }

        // Fletching - three feathers
        for(angle = [0:120:240]) {
            rotate([0, 0, angle])
            translate([0, 0, overall_length - 5])
            color([0.8, 0.8, 0.8])
            linear_extrude(height=0.5, center=true)
            polygon(points=[
                [0, 0],
                [8, 3],
                [15, 2],
                [20, 0],
                [15, -2],
                [8, -3]
            ]);
        }
    }
}

