// Realistic historical chakram for 3D printing
// Medium complexity with authentic details

$fn = 64;  // Smooth curves for 3D printing

// Main parameters
chakram_diameter = 80;
chakram_thickness = 4;
inner_hole = 35;
edge_thickness = 2;

module chakram_ring() {
    // Outer ring with slight taper
    difference() {
        cylinder(h = chakram_thickness, d = chakram_diameter, center = true);
        translate([0, 0, -0.5])
            cylinder(h = chakram_thickness + 1, d = inner_hole, center = true);
    }
}

module sharpened_edge() {
    // Traditional double-edged blade profile
    color("silver")
    for (i = [0:1]) {
        rotate([0, 0, i * 180])
        translate([0, 0, chakram_thickness/2 - edge_thickness/2])
        difference() {
            cylinder(h = edge_thickness, d = chakram_diameter, center = true);
            cylinder(h = edge_thickness + 1, d = chakram_diameter - 4, center = true);
        }
    }
}

module decorative_panels() {
    // Traditional 8-panel design with engravings
    color("silver")
    for (angle = [0:45:315]) {
        rotate([0, 0, angle])
        translate([chakram_diameter/2 - 6, 0, -chakram_thickness/2 - 0.1])
        difference() {
            cube([6, 3, chakram_thickness + 0.2], center = true);
            // Engraved center line
            translate([3, 0, 0])
                cube([0.5, 2, chakram_thickness + 0.4], center = true);
        }
    }
}

module central_mandala() {
    // Traditional sun symbol in center
    color("goldenrod")
    translate([0, 0, chakram_thickness/2 + 0.1])
    for (rays = [0:30:330]) {
        rotate([0, 0, rays])
        translate([inner_hole/2 + 2, 0, 0])
            cube([4, 0.8, 0.3], center = true);
    }
    // Central dot
    translate([0, 0, chakram_thickness/2 + 0.2])
        cylinder(h = 0.5, d = 3, center = true);
}

module grip_pattern() {
    // Textured grip on inner edge
    color("silver")
    for (grip = [0:15:345]) {
        rotate([0, 0, grip])
        translate([(inner_hole + chakram_diameter)/4, 0, 0])
            rotate([90, 0, 0])
            cylinder(h = 3, d = 1.5, center = true);
    }
}

module inscription_band() {
    // Faux Devanagari-inspired inscription
    color("silver")
    translate([0, 0, chakram_thickness/2 + 0.05])
    for (i = [0:5:355]) {
        rotate([0, 0, i])
        translate([chakram_diameter/2 - 2, 0, 0])
            cube([1, 0.5, 0.1], center = true);
    }
}

// Assembly
union() {
    chakram_ring();
    sharpened_edge();
    decorative_panels();
    central_mandala();
    grip_pattern();
    inscription_band();
}

// Realistic historical shuriken (hira-shuriken) with medium complexity
// Based on Edo-period Japanese throwing stars

// Main parameters
shuriken_diameter = 80;
thickness = 3;
blade_width = 8;
center_hole_diameter = 12;

// Colors for realistic appearance
metal_color = "#8B7355";  // Aged iron color
edge_color = "#A0A0A0";   // Polished steel edge highlight

// Module for a single blade point
module blade_point(length, width, thickness) {
    // Create blade taper
    difference() {
        // Main blade body
        linear_extrude(height = thickness, center = true) {
            polygon(points = [
                [0, 0],
                [width/2, -length * 0.2],
                [width/3, -length * 0.8],
                [0, -length],
                [-width/3, -length * 0.8],
                [-width/2, -length * 0.2]
            ]);
        }

        // Create hollow ground blade edge
        translate([0, -length * 0.5, 0])
            cube([width * 0.6, length * 0.4, thickness + 1], center = true);
    }

    // Add edge bevel
    color(edge_color)
    translate([0, -length * 0.1, thickness/2 + 0.1])
        cube([width * 0.8, length * 0.8, 0.2], center = true);
}

// Module for decorative pattern on blade
module blade_pattern() {
    // Traditional kanji family mark (placeholder geometric pattern)
    for (i = [0:3]) {
        rotate([0, 0, i * 90])
        translate([0, -shuriken_diameter/3, thickness/2 + 0.1])
            linear_extrude(height = 0.3)
                circle(r = 3, $fn = 6);
    }
}

// Module for central ring with grip texture
module central_ring() {
    difference() {
        // Main ring
        cylinder(h = thickness + 2, r = center_hole_diameter/2 + 3, center = true, $fn = 32);

        // Center hole
        cylinder(h = thickness + 3, r = center_hole_diameter/2, center = true, $fn = 32);
    }

    // Add grip rings
    for (i = [-1, 0, 1]) {
        translate([0, 0, i * 0.5])
            cylinder(h = 0.3, r = center_hole_diameter/2 + 2, center = true, $fn = 32);
    }
}

// Create the complete shuriken
union() {
    color(metal_color) {
        // Main body with all four blades
        difference() {
            // Create central disk
            cylinder(h = thickness, r = shuriken_diameter/2, center = true, $fn = 32);

            // Cut out spaces between blades
            for (i = [0:3]) {
                rotate([0, 0, i * 90 + 45])
                    translate([0, shuriken_diameter/4, 0])
                        cube([blade_width * 1.5, shuriken_diameter/2, thickness + 2], center = true);
            }
        }

        // Add four blade points
        for (i = [0:3]) {
            rotate([0, 0, i * 90])
                translate([0, shuriken_diameter/2 - 5, 0])
                    blade_point(shuriken_diameter/2 - 10, blade_width, thickness);
        }
    }

    // Add central ring
    color(metal_color)
    central_ring();

    // Add decorative patterns
    blade_pattern();
}

// Add subtle wear marks
for (i = [0:7]) {
    color("#6B5B47")
    rotate([0, 0, i * 45])
    translate([shuriken_diameter/3, 0, thickness/2 + 0.2])
        scale([1, 0.3, 0.1])
            sphere(r = 2);
}

// Historical Kunai - Realistic Style
// Based on traditional Japanese kunai tool/weapons used by ninjas
// Features include ring pommel, wrapped handle, leaf-shaped blade, and decorative elements

// Overall dimensions: 100mm length, scaled for 3D printing

module kunai() {
  color("gray") {
    // Ring pommel (characteristic of historical kunai)
    translate([0, 0, 0]) {
      difference() {
        cylinder(h=8, d=25, $fn=64);
        cylinder(h=10, d=20, $fn=64);
      }
    }

    // Handle with wrapped cord texture
    translate([0, 0, 8]) {
      union() {
        // Main handle cylinder
        cylinder(h=35, d=15, $fn=32);

        // Cord wrapping pattern
        for (i = [0:2:30]) {
          translate([0, 0, 10 + i]) {
            rotate([90, 0, 0]) {
              cylinder(h=20, d=16.5, $fn=6);
            }
          }
        }
      }
    }

    // Blade base (transition from handle to blade)
    translate([0, 0, 43]) {
      cylinder(h=5, d1=15, d2=20, $fn=32);
    }

    // Main blade - leaf-shaped with historical accuracy
    translate([0, 0, 48]) {
      // Front face of blade
      difference() {
        union() {
          // Main blade body
          linear_extrude(height=2) {
            polygon(points=[
              [0, 0],
              [-10, 40],
              [0, 50],
              [10, 40],
              [0, 0]
            ]);
          }

          // Spine reinforcement
          translate([0, 25, 0]) {
            cube([3, 25, 2], center=true);
          }
        }

        // Fuller (blood groove) - historically accurate feature
        translate([0, 30, -0.5]) {
          rotate([0, 0, 0]) {
            cube([1.5, 20, 3], center=true);
          }
        }
      }

      // Back face of blade
      translate([0, 0, 2]) {
        linear_extrude(height=2) {
          polygon(points=[
            [0, 0],
            [-10, 40],
            [0, 50],
            [10, 40],
            [0, 0]
          ]);
        }
      }
    }

    // Decorative tassel attachment point on ring
    translate([0, 0, -2]) {
      color("brown") {
        for (i = [0:120:360]) {
          rotate([0, 0, i]) {
            translate([11.5, 0, 0]) {
              cylinder(h=3, d=1.5, $fn=8);
            }
          }
        }
      }
    }

    // Historical inscriptions on handle
    translate([0, 8, 25]) {
      rotate([90, 0, 0]) {
        color("black") {
          // Simple kanji-like markings
          for (i = [0:3]) {
            translate([0, 3*i - 4.5, 0]) {
              cube([0.5, 2, 0.2], center=true);
            }
          }
        }
      }
    }
  }
}

// Scale to appropriate 3D printing size
scale(0.8) {
  kunai();
}

// Katar (Indian push dagger) - realistic style, medium complexity
// Overall length ~85mm, optimized for 3D printing

// Parameters
blade_length = 55;
blade_width = 20;
blade_thickness = 3;
handle_length = 30;
handle_width = 60;
handle_height = 15;
bar_thickness = 4;
wall_thickness = 2;

// Main blade
module katar_blade() {
    color([0.7, 0.7, 0.7])  // Steel color
    union() {
        // Tapered blade
        linear_extrude(height = blade_thickness, center = true, scale = [0.3, 1], slices = 20)
        polygon(points = [
            [0, 0],
            [blade_length, -blade_width/2],
            [blade_length, blade_width/2],
            [0, 0]
        ]);

        // Central ridge
        translate([0, 0, 0])
        cube([blade_length*0.9, 1, blade_thickness+0.5], center = true);
    }
}

// Decorative side bars with traditional patterns
module side_bars() {
    color([0.6, 0.5, 0.4])  // Bronze color
    for(side = [-1, 1]) {
        translate([0, side * (handle_width/2 - bar_thickness/2), 0])
        cube([handle_length, bar_thickness, handle_height], center = true);

        // Decorative rings
        for(i = [1:4]) {
            translate([handle_length/2 - i*6, side * (handle_width/2 - bar_thickness/2), handle_height/2 - 2])
            rotate([90, 0, 0])
            cylinder(h = bar_thickness+1, r = 2, $fn = 16);
        }
    }
}

// Hand grip
module hand_grip() {
    color([0.4, 0.3, 0.2])  // Dark wood color
    difference() {
        // Main grip
        translate([handle_length/2, 0, -handle_height/4])
        cube([handle_length, handle_width - bar_thickness*2, handle_height/2], center = true);

        // Finger grooves
        for(i = [0:3]) {
            translate([handle_length/2 - 8 + i*5, 0, -handle_height/4])
            cylinder(h = handle_width - bar_thickness*2, r = 3, $fn = 16);
        }
    }
}

// Decorative pommel plate
module pommel_plate() {
    color([0.6, 0.5, 0.4])  // Bronze color
    translate([-handle_length/2 - 2, 0, 0])
    difference() {
        cube([4, handle_width, handle_height], center = true);
        // Decorative cutouts
        for(x = [-12:12:12]) {
            for(y = [-20:20:20]) {
                translate([0, x, y])
                cylinder(h = 5, r = 3, $fn = 6);
            }
        }
    }
}

// Assembly
module katar() {
