// Utility module for rounded edges
module rounded_box(x, y, z, r) {
    translate([r, r, 0]) cube([x-2*r, y-2*r, z]);
    translate([r, r, 0]) cylinder(h=z, r=r, $fn=16);
    translate([x-r, r, 0]) cylinder(h=z, r=r, $fn=16);
    translate([r, y-r, 0]) cylinder(h=z, r=r, $fn=16);
    translate([x-r, y-r, 0]) cylinder(h=z, r=r, $fn=16);
}

// Blade with fuller
module blade() {
    color(blade_color) {
        union() {
            // Main blade
            translate([0, -blade_width/2, 0])
                cube([blade_length, blade_width, blade_thickness]);

            // Fuller (blood groove)
            translate([blade_length/2, -blade_width/4, blade_thickness/2])
                cube([blade_length*0.7, blade_width/2, blade_thickness/0.5]);

            // Tip
            translate([blade_length-10, -blade_width/2, 0])
                rotate([0, 0, -atan(blade_width/20)])
                cube([20, 20, blade_thickness]);
        }
    }
}

// Guard with decorative elements
module guard() {
    color(guard_color) {
        union() {
            // Main guard plate
            translate([-5, -guard_width/2, -guard_height/2])
                rounded_box(guard_length, guard_width, 5, 3);

            // Decorative side prongs
            for(i = [-1, 1]) {
                translate([guard_length/2-10, i*guard_width/3, 0])
                    rotate([0, 0, i*15])
                    cube([15, 5, 5]);
            }

            // Central decorative element
            translate([guard_length/2, 0, 0])
                cylinder(h=8, r=8, $fn=32);
        }
    }
}

// Gauntlet with articulated plates
module gauntlet() {
    color(gauntlet_color) {
        difference() {
            // Main gauntlet body
            translate([0, -gauntlet_width/2, -gauntlet_height/2])
                rounded_box(gauntlet_length, gauntlet_width, gauntlet_height, 5);

            // Hand opening
            translate([-5, -gauntlet_width/4, -gauntlet_height/2])
                cube([gauntlet_length/2, gauntlet_width/2, gauntlet_height]);

            // Knuckle relief
            for(i = [-1, 0, 1]) {
                translate([gauntlet_length*0.7, i*gauntlet_width/6, 0])
                    sphere(r=5, $fn=16);
            }
        }

        // Decorative rivets
        for(i = [0:2]) {
            translate([i*gauntlet_length/3, -gauntlet_width/2+5, gauntlet_height/2-2])
                color("darkgoldenrod") cylinder(h=3, r=2, $fn=16);
            translate([i*gauntlet_length/3, gauntlet_width/2-5, gauntlet_height/2-2])
                color("darkgoldenrod") cylinder(h=3, r=2, $fn=16);
        }
    }
}

// Handle
module handle() {
    color(handle_color) {
        // Main grip
        translate([-handle_length/2, -gauntlet_width/4, -gauntlet_height/4])
            rounded_box(handle_length, gauntlet_width/2, gauntlet_height/2, 3);

        // Grip texture rings
        for(i = [0:3]) {
            translate([-handle_length/2 + i*handle_length/3, -gauntlet_width/4, -gauntlet_height/4])
                cylinder(h=2, r=gauntlet_width/4+1, $fn=16);
        }
    }
}

// Assembly
module pata_sword() {
    // Blade assembly
    translate([0, 0, 0]) blade();

    // Guard
    translate([-guard_length+5, 0, blade_thickness/2]) rotate([90, 0, 0]) guard();

    // Gauntlet
