                [0, blade_width*0.3]       // Tang end
            ]);

            // Fuller groove
            translate([blade_length*0.4, blade_width*0.5, -1])
            scale([1, 0.3, 1])
            cylinder(h=blade_thickness+2, r=blade_length*0.3, $fn=20);
