color_handle = [0.4, 0.2, 0.1];  // Dark wood
color_guard = [0.8, 0.8, 0.2];  // Brass/gold
color_base = [0.3, 0.3, 0.3];  // Dark stone

module krisbow() {
    // Base pedestal
    color(color_base) {
        cylinder(h=base_height, r1=guard_size/2+5, r2=guard_size/2+8, $fn=32);
    }

    // Guard/water buffalo horn detail
    translate([0, 0, base_height]) {
        color(color_guard) {
            cylinder(h=2, r=guard_size/2, $fn=32);

            // Decorative ring details
            for(i=[0:1:3]) {
                translate([0, 0, 2+i*0.3]) {
                    cylinder(h=0.5, r=guard_size/2 + i*0.5, $fn=32);
                }
            }
        }
    }

    // Handle
    translate([0, 0, base_height+3.5]) {
        color(color_handle) {
            // Main handle body - curved
            for(i=[0:1:handle_length*2]) {
                translate([sin(i*5)*1.5, 0, i/2]) {
                    hull() {
                        sphere(d=handle_width - abs(i-handle_length)/handle_length*4, $fn=16);
                        translate([0, 0, 0.5]) sphere(d=handle_width - abs(i-handle_length)/handle_length*4, $fn=16);
                    }
                }
            }

            // Decorative rings
            for(i=[1:3]) {
                translate([0, 0, base_height+3.5 + i*handle_length/4]) {
                    color(color_guard) {
                        cylinder(h=1, r=handle_width/2+1, $fn=32);
                    }
                }
            }
        }
    }

    // Blade
    translate([0, 0, base_height+3.5+handle_length]) {
        color(color_blade) {
            // Main blade with kris wavy pattern
            for(wave=[0:1:blade_length*2]) {
                angle = wave * 15;
                wave_offset = sin(wave/3) * blade_width/3;
                translate([wave_offset, 0, wave/2]) {
                    // Blade cross-section
                    hull() {
                        sphere(d=blade_thickness, $fn=8);
                        translate([blade_width - abs(wave-blade_length)/blade_length*blade_width/2, 0, 0]) 
                            sphere(d=blade_thickness, $fn=8);
                    }
                }
            }

            // Blood groove
            difference() {
                translate([blade_length/2, 0, blade_length/2]) {
                    cube([blade_length, blade_width/10, blade_length], center=true);
                }

                // Subtle groove
                translate([blade_length/2, 0, blade_length/2]) {
                    cube([blade_length-4, blade_width/8, blade_length-4], center=true);
                }
            }

            // Tip
            translate([sin(blade_length*2/3)*blade_width/3, 0, blade_length]) {
                cylinder(h=8, r1=blade_thickness/2, r2=0, $fn=16);
            }
        }
    }
}

// Main assembly
krisbow();

// Support base for printing
translate([0, 0, -2]) {
    color([0.2, 0.2, 0.2]) {
        cylinder(h=2, r=guard_size/2+8, $fn=32);
