}

// Composite Bow - Historical Artifact (Mongol/Turkic style, 13th century)
// Realistic style with medium complexity
// Designed for 3D printing, 80mm length

$fn = 64;

// Overall dimensions
bow_length = 80;
bow_height = 25;
bow_thickness = 4;

// Color definitions
wood_color = [139/255, 90/255, 43/255];  // Dark brown wood
horn_color = [51/255, 25/255, 0/255];    // Dark horn
sinew_color = [240/255, 234/255, 214/255]; // Cream sinew
metal_color = [150/255, 150/255, 150/255]; // Iron
gold_color = [212/255, 175/255, 55/255];   // Gold decoration

// Main bow module
module composite_bow() {
    // Bow limbs
    union() {
        // Upper limb
        translate([0, 0, bow_thickness/2])
            bow_limb();

        // Lower limb (mirrored)
        translate([0, 0, -bow_thickness/2])
            mirror([0, 0, 1])
                bow_limb();

        // Grip/handle section
        grip_section();

        // Decorative end caps
        end_caps();

        // Sinew wrapping details
        sinew_wrappings();

        // Decorative inlays
        decorative_inlays();
    }
}

// Bow limb construction
module bow_limb() {
    // Core wood layer
    color(wood_color)
    difference() {
        // Main curved shape
        hull() {
            // Tip
            translate([bow_length/2-3, 0, 0])
                scale([1, 0.8, 1])
                    sphere(2);

            // Mid-section
            translate([bow_length/4, bow_height/3, 0])
                sphere(3);

            // Grip transition
            translate([0, bow_height/2, 0])
                sphere(2.5);
        }

        // Hollow center for weight reduction
        translate([bow_length/4, bow_height/3, 0])
            scale([0.6, 0.6, 0.6])
                sphere(2.5);
    }

    // Horn layer (belly of bow)
    color(horn_color)
    translate([0, 0, -0.5])
    difference() {
        hull() {
            translate([bow_length/2-3, 0, -0.5])
                scale([0.9, 0.7, 0.5])
                    sphere(1.8);
            translate([bow_length/4, bow_height/3, -0.5])
                scale([0.9, 0.9, 0.5])
                    sphere(2.5);
            translate([0, bow_height/2, -0.5])
                scale([0.9, 0.9, 0.5])
                    sphere(2);
        }

        // Remove wood core overlap
        translate([bow_length/4, bow_height/3, -0.5])
            scale([0.5, 0.5, 0.5])
                sphere(2.5);
    }

    // Sinew backing layer
    color(sinew_color)
    translate([0, 0, 0.5])
    difference() {
        hull() {
            translate([bow_length/2-3, 0, 0.5])
                scale([0.95, 0.85, 0.3])
                    sphere(1.9);
            translate([bow_length/4, bow_height/3, 0.5])
                scale([0.95, 0.95, 0.3])
                    sphere(2.7);
            translate([0, bow_height/2, 0.5])
                scale([0.95, 0.95, 0.3])
                    sphere(2.2);
        }

        // Remove wood core overlap
        translate([bow_length/4, bow_height/3, 0.5])
            scale([0.55, 0.55, 0.3])
                sphere(2.7);
    }
}

// Grip section
module grip_section() {
    color(wood_color)
    difference() {
        // Main grip
        hull() {
            translate([-bow_length/8, bow_height/2, 0])
                sphere(2.5);
            translate([bow_length/8, bow_height/2, 0])
                sphere(2.5);
            translate([0, bow_height/2 + 2, 0])
                sphere(2);
        }

        // Ergonomic finger grooves
        for(i = [-1:1]) {
            translate([i * 2, bow_height/2 + 1, 0])
                rotate([0, 90, 0])
                    cylinder(h=4, r=0.8, $fn=16);
        }
    }

    // Leather wrapping texture
    color([100/255, 70/255, 40/255])
    for(i = [0:5]) {
        translate([-bow_length/8 + i*2, bow_height/2, 0])
            rotate([90, 0, 0])
                cylinder(h=0.3, r=2.5, $fn=20);
    }
}

// Decorative end caps
module end_caps() {
    // Upper cap (siyah)
    color(metal_color)
    translate([bow_length/2-3, 0, 0])
    difference() {
        union() {
            // Cap body
            cylinder(h=4, r1=2.2, r2=1.8, $fn=16);
            // Decorative ring
            translate([0, 0, 2])
                cylinder(h=0.5, r=2.4, $fn=16);
        }

        // String groove
        translate([0, 1.5, 2])
            cube([3, 0.8, 0.8], center=true);
    }

    // Gold inlay decoration
    color(gold_color)
    translate([bow_length/2-3, 0, 1])
        cylinder(h=0.3, r=2, $fn=16);
}

// Sinew wrappings at joints
module sinew_wrappings() {
    color(sinew_color)
    // Wrapping at grip transition
    for(i = [0:3]) {
        translate([-i*1.5 - 2, bow_height/2 - 0.5, 0])
            rotate([90, 0, 0])
                cylinder(h=0.5, r=2.8, $fn=12);
    }

    // Wrapping at limb tips
    for(side = [-1, 1]) {
        translate([side * (bow_length/2 - 5), side * bow_height/8, 0])
            for(i = [0:2]) {
                rotate([0, 0, side * 30])
                    translate([0, 0, i*0.4 - 0.4])
                        rotate([90, 0, 0])
                            cylinder(h=0.3, r=2, $fn=12);
            }
    }
}

// Decorative inlays
module decorative_inlays() {
    // Mother-of-pearl inlays on limbs
    color([0.9, 0.9, 0.85])
    for(i = [1:3]) {
        translate([i * 10, bow_height/2 + 1, 0.8])
            rotate([0, 0, 15])
                scale([1, 0.6, 1])
                    cylinder(h=0.2, r=1.5, $fn=6);
    }

    // Traditional geometric patterns
    color(gold_color)
    for(i = [1:3]) {
        translate([i * 10 - 5, bow_height/2 - 0.5, 0.8])
            cube([1.5, 0.2, 0.2], center=true);
    }
}

// Main assembly
composite_bow();

// Historical English longbow (c. 1350-1450)
// Based on Mary Rose artifacts

bow_length = 90;
bow_thickness = 3;
grip_length = 15;
grip_width = 5;
nock_radius = 1.5;
nock_depth = 3;
string_diameter = 0.8;
decoration_band_width = 2;
decoration_band_thickness = 0.2;

module bow_stave() {
    // Main stave body
    difference() {
        hull() {
            // Upper limb
            translate([0, 0, bow_length/2 - grip_length/2])
                scale([1, 1, (bow_length - grip_length)/2])
                    cylinder(h = 1, r = bow_thickness/2, $fn = 16);
            // Lower limb
            translate([0, 0, -bow_length/2 + grip_length/2])
                scale([1, 1, (bow_length - grip_length)/2])
                    cylinder(h = 1, r = bow_thickness/2, $fn = 16);
            // Grip section
            translate([0, 0, 0])
                cube([grip_width, bow_thickness*1.2, grip_length], center = true);
        }

        // Taper limbs
        for(i = [0:1]) {
            mirror([0, 0, i == 0 ? 1 : 0]) {
                translate([0, 0, bow_length/2 - grip_length/2 - 5])
                    rotate([0, 90, 0])
                        cylinder(h = bow_thickness*2, r1 = bow_thickness*0.8, r2 = bow_thickness*0.4, $fn = 16);
            }
        }
    }
}

module bow_nocks() {
    for(i = [0:1]) {
        mirror([0, 0, i == 0 ? 1 : 0]) {
            // Upper nock
            translate([0, bow_thickness/2, bow_length/2 - nock_depth/2]) {
                difference() {
                    cylinder(h = nock_depth, r = nock_radius, $fn = 12);
                    translate([0, -nock_radius/2, nock_depth/2])
                        cube([nock_radius*2, nock_radius, nock_depth+1], center = true);
                }
            }
        }
    }
}

module bow_string() {
    color("black") {
        translate([0, bow_thickness/2 + string_diameter/2, 0]) {
            rotate([90, 0, 0]) {
                cylinder(h = bow_length - nock_depth*2, r = string_diameter/2, $fn = 8);
            }
        }
    }
}

module decorative_bands() {
    color("gold") {
        // Upper decorative band
        translate([0, 0, grip_length/2 + decoration_band_width/2])
            cube([grip_width + 1, decoration_band_thickness, decoration_band_width], center = true);

        // Lower decorative band
        translate([0, 0, -grip_length/2 - decoration_band_width/2])
            cube([grip_width + 1, decoration_band_thickness, decoration_band_width], center = true);

        // Arrow pass reinforcement
        translate([grip_width/2 + 0.5, 0, 0])
            cube([1, decoration_band_thickness, grip_length/3], center = true);
    }
}

module bow() {
    color("saddlebrown") {
        union() {
            bow_stave();
            bow_nocks();
            decorative_bands();
        }
    }
    bow_string();
}

// Assemble the bow
rotate([90, 0, 0]) {
    bow();
}

// Historical Shortbow - Scythian Composite Bow Replica (5th-3rd Century BCE)
// Medium complexity realistic style with artifact details

$fn = 32;

// Main bow dimensions
