horn_color = [50/255, 50/255, 50/255];
sinew_color = [250/255, 240/255, 230/255];
metal_color = [184/255, 115/255, 51/255];

// Main bow body with characteristic Scythian recurve
module bow_body() {
    color(wood_color) {
        // Upper limb
        translate([0, bow_length/2, 0])
            rotate([0, 15, 0])
            scale([1, 1.2, 1])
            cylinder(h=bow_length/2, r1=bow_width/2, r2=bow_width/3);

        // Lower limb
        translate([0, -bow_length/2, 0])
            rotate([0, -15, 0])
            scale([1, 1.2, 1])
            cylinder(h=bow_length/2, r1=bow_width/2, r2=bow_width/3);

