        // Upper siyah (tip reinforcement)
        translate([0, bow_length/2 + 8, 0])
            rotate([0, 30, 0])
            scale([0.8, 0.3, 0.6])
            cylinder(h=10, r1=bow_width/3, r2=bow_width/4);

        // Lower siyah
        translate([0, -bow_length/2 - 8, 0])
            rotate([0, -30, 0])
            scale([0.8, 0.3, 0.6])
            cylinder(h=10, r1=bow_width/3, r2=bow_width/4);
