        // Central grip decorations
        for(i = [-1:1]) {
            translate([bow_width/2 + 1, i*5, 0])
                scale([0.5, 0.5, 0.3])
                sphere(2);
        }

        // Limb tip decorations
        for(pos = [bow_length/2 - 5, -bow_length/2 + 5]) {
            translate([0, pos, bow_thickness/2 + 0.5])
                scale([1, 0.8, 0.2])
                cylinder(h=1, r=2);
        }
