// Recurve Composite Bow - Scythian Style (circa 500 BCE)
// Medium complexity realistic model with historical details

$fn = 32;

// Overall dimensions
bow_length = 80;
bow_height = 35;
grip_width = 8;
limb_thickness = 3;

// Colors
wood_color = [139/255, 69/255, 19/255]; // saddle brown
horn_color = [255/255, 250/255, 240/255]; // floral white
sinew_color = [245/255, 245/255, 220/255]; // beige
decoration_color = [218/255, 165/255, 32/255]; // goldenrod

module bow_limb(side) {
    // Main limb curve with recurve tip
    translate([0, side * (bow_height/2 - 5), 0])
    rotate([0, 0, side * -30])
    union() {
        // Upper limb
        color(wood_color)
        difference() {
            // Tapered limb
            hull() {
                translate([bow_length/4, 0, 0])
                scale([1, 0.8, 1])
                sphere(r=limb_thickness);

                translate([-bow_length/4, side * (bow_height/2 - 8), 0])
                sphere(r=limb_thickness * 0.7);
            }

            // Hollow core
            translate([0, 0, -limb_thickness/2 - 0.5])
            cube([bow_length/2 + 5, bow_height/2, limb_thickness + 1], center=true);
        }

        // Horn overlay on belly
        color(horn_color)
        translate([0, -side * 0.5, limb_thickness/2 - 0.2])
        scale([1, 0.8, 0.3])
        hull() {
            translate([bow_length/4 - 5, 0, 0])
            sphere(r=limb_thickness * 0.8);

            translate([-bow_length/4 + 3, side * (bow_height/2 - 8), 0])
            sphere(r=limb_thickness * 0.5);
        }

        // Recurve tip
        color(horn_color)
        translate([-bow_length/4 - 3, side * (bow_height/2 - 5), 0])
        rotate([0, 90, side * 20])
        cylinder(h=8, r1=limb_thickness * 0.6, r2=limb_thickness * 0.3);

        // Decorative string bridges
        for(i = [1:3]) {
            color(decoration_color)
            translate([bow_length/4 - i*7, side * (bow_height/2 - 8 + i*2), 0])
            rotate([90, 0, 0])
            cylinder(h=limb_thickness + 0.4, r=0.5);
        }
    }
}

module bow_grip() {
    // Central grip section
    color(wood_color)
    difference() {
        // Grip handle
        hull() {
            translate([0, 0, 0])
            sphere(r=grip_width/2);

            translate([bow_length/6, 0, 0])
            scale([1.2, 0.8, 1])
            sphere(r=grip_width/2);

            translate([-bow_length/6, 0, 0])
            scale([1.2, 0.8, 1])
            sphere(r=grip_width/2);
        }

        // Finger groove
        translate([0, 0, -grip_width/2 - 1])
        cube([bow_length/3 + 2, grip_width, grip_width + 2], center=true);
    }

    // Leather wrap texture
    color(sinew_color)
    for(i = [-2:2]) {
        translate([i * 3, 0, grip_width/2 - 0.2])
        rotate([90, 0, 0])
        cylinder(h=0.5, r=grip_width/2 + 0.2);
    }

    // Decorative end caps
    color(decoration_color)
    translate([bow_length/4 + 2, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=1.5, r=grip_width/2);

    translate([-bow_length/4 - 2, 0, 0])
    rotate([0, 90, 0])
    cylinder(h=1.5, r=grip_width/2);
}

module bow_string() {
    // Simple string representation
    color([0, 0, 0])
    translate([0, 0, limb_thickness/2 + 0.5])
    rotate([0, 0, 0])
    hull() {
        translate([bow_length/4, bow_height/2 - 5, 0])
        sphere(r=0.3);

        translate([bow_length/4, -bow_height/2 + 5, 0])
        sphere(r=0.3);
    }
}

// Assembly
translate([0, 0, limb_thickness/2])
union() {
    bow_grip();
    bow_limb(1);  // Upper limb
    bow_limb(-1); // Lower limb
    bow_string();
}

