// Bow stave module with natural taper
module bow_stave() {
    color(color_bow)
    union() {
        // Handle section - thicker central part
        translate([-handle_length/2, 0, 0])
            cube([handle_length, handle_width, bow_thickness], center=true);

        // Upper limb - tapered
        translate([handle_length/2 + 15, 0, 0])
            scale([1, 0.7, 1])
            cube([30, 6, bow_thickness], center=true);

        // Lower limb - tapered
            translate([-handle_length/2 - 15, 0, 0])
            scale([1, 0.7, 1])
            cube([30, 6, bow_thickness], center=true);
    }
