// Decorative handle wrap
module handle_wrapping() {
    color(color_handle)
    for (i = [-3:3]) {
        translate([i * 2.5, 0, bow_thickness/2 + 0.5])
            rotate([90, 0, 0])
            cylinder(h=handle_width, d=1, $fn=8);
