// Historical knot detail
module decorative_knot() {
    translate([handle_length/2 - 5, 0, bow_thickness/2 + 1])
    color(color_handle)
    difference() {
        cylinder(h=2, d=4, $fn=20);
        translate([0, 0, -0.1])
            cylinder(h=2.2, d=2, $fn=20);
    }
