// Bow string with historical serving
module bow_string() {
    color(color_string)
    translate([0, 0, bow_thickness/2 + 0.5])
    union() {
        // Main string
        for (side = [-1, 1]) {
            translate([0, side * 3.5, 0])
                cylinder(h=bow_length, d=0.5, $fn=8);
        }

        // Serving at center
        translate([-5, 0, 0])
            for (i = [0:4]) {
                translate([i, 0, 0])
                    cylinder(h=10, d=1.2, $fn=8);
            }
    }
}

// Nock grooves
module nock_grooves() {
    color(color_bow)
    for (end = [-1, 1]) {
        translate([end * (bow_length/2 - 2), 0, 0])
            rotate([90, 0, 0])
            difference() {
                cylinder(h=6, d=3, $fn=20);
                translate([0, 0, -0.1])
                    cylinder(h=6.2, d=2, $fn=20);
            }
