// Historical Yumi (Japanese Asymmetrical Bow) - Realistic Style

// Overall scale factor for 3D printing (target ~70mm height)
scale_factor = 0.15;

// Main bow parameters
bow_height = 70 * scale_factor;
bow_width = 45 * scale_factor;
bow_thickness = 3 * scale_factor;
grip_offset = 15 * scale_factor; // Asymmetrical grip position

// Colors
bamboo_color = [194/255, 154/255, 108/255];
lacquer_color = [139/255, 69/255, 19/255];
leather_color = [101/255, 67/255, 33/255];
gold_color = [212/255, 175/255, 55/255];

// Module for decorative grip wrapping
module grip_wrapping() {
    color(leather_color) {
        // Main grip area
        difference() {
            translate([0, grip_offset, 0])
                cube([bow_thickness * 3, bow_height * 0.15, bow_thickness * 2], center=true);

            // Ridges for texture
            for(i = [-2:2]) {
                translate([0, grip_offset + i * 3 * scale_factor, 0])
                    cube([bow_thickness * 3.2, 1 * scale_factor, bow_thickness * 2.2], center=true);
            }
        }

        // Binding cords
        color([0.8, 0.8, 0.8]) {
            for(j = [0:5]) {
                translate([0, grip_offset - 8 * scale_factor + j * 3 * scale_factor, bow_thickness])
                    rotate([90, 0, 0])
                    cylinder(h=bow_thickness * 3, r=0.3 * scale_factor, center=true);
            }
        }
    }
}

// Module for bow limbs
module bow_limb(side) {
    // Main limb curve
    color(bamboo_color) {
        difference() {
            // Upper limb (longer)
            if(side == "upper") {
                translate([0, bow_height/2 - grip_offset, 0])
                    scale([1, 1.8, 1])
                    rotate([0, 0, side == "upper" ? 15 : -15])
                    cylinder(h=bow_height * 0.6, r1=bow_thickness * 1.5, r2=bow_thickness, center=true);
            } else {
                // Lower limb (shorter)
                translate([0, -bow_height/2 - grip_offset, 0])
                    scale([1, 1.2, 1])
                    rotate([0, 0, -15])
                    cylinder(h=bow_height * 0.4, r1=bow_thickness * 1.5, r2=bow_thickness, center=true);
            }
        }
    }

    // Lacquer coating
    color(lacquer_color) {
        if(side == "upper") {
            translate([0, bow_height/2 - grip_offset + bow_height * 0.3, 0])
                rotate([0, 0, 15])
                cube([bow_thickness * 3.2, bow_height * 0.6, bow_thickness * 2.2], center=true);
        } else {
            translate([0, -bow_height/2 - grip_offset - bow_height * 0.2, 0])
                rotate([0, 0, -15])
                cube([bow_thickness * 3.2, bow_height * 0.4, bow_thickness * 2.2], center=true);
        }
    }
}

// Module for string (simplified for 3D printing)
module bow_string() {
    color([0.9, 0.9, 0.9]) {
        hull() {
            // Upper nock point
            translate([0, bow_height/2 - grip_offset + bow_height * 0.3, 0])
                sphere(0.5 * scale_factor);
            // Lower nock point
            translate([0, -bow_height/2 - grip_offset - bow_height * 0.2, 0])
                sphere(0.5 * scale_factor);
        }
    }
}

// Module for decorative mon (family crest) on grip
module mon_crest() {
    color(gold_color) {
        // Simple chrysanthemum mon
        for(i = [0:15]) {
            rotate([0, 0, i * 22.5])
            translate([0, grip_offset, bow_thickness * 1.1])
                sphere(0.8 * scale_factor);
        }
        translate([0, grip_offset, bow_thickness * 1.2])
            sphere(1 * scale_factor);
    }
}

// Module for bow tips (nocks)
module bow_tip(side) {
    color(bamboo_color) {
        y_pos = side == "upper" ? 
            bow_height/2 - grip_offset + bow_height * 0.3 : 
            -bow_height/2 - grip_offset - bow_height * 0.2;

        translate([0, y_pos, 0])
            cylinder(h=bow_thickness * 2, r1=bow_thickness * 1.2, r2=bow_thickness * 0.8, center=true);

        // Horn nock reinforcement
        color([0.3, 0.3, 0.3]) {
            translate([0, y_pos + (side == "upper" ? 1 : -1) * bow_thickness, 0])
                cube([bow_thickness * 0.5, bow_thickness * 0.5, bow_thickness * 1.5], center=true);
        }
    }
}

// Main assembly
union() {
    // Central grip section
    color(bamboo_color) {
        translate([0, 0, 0])
            cube([bow_thickness * 3, bow_height * 0.2, bow_thickness * 2], center=true);
    }

    // Upper limb
    bow_limb("upper");

    // Lower limb
    bow_limb("lower");

    // Grip wrapping
    grip_wrapping();

    // Bow string (simplified)
    bow_string();

    // Decorative mon
    mon_crest();

    // Bow tips
    bow_tip("upper");
    bow_tip("lower");
}

