            rotate([90, 0, 0])
                linear_extrude(height=1)
                    text("LEG", size=3, halign="center");
    }

    // Square shank (prevents rotation when installed)
    translate([0, 0, -shank_length])
        difference() {
            cube([square_shank_width, square_shank_width, shank_length], center=true);

            // Rounded corners for printability
            for(i = [-1, 1])
                for(j = [-1, 1])
                    translate([i*square_shank_width/2, j*square_shank_width/2, -shank_length/2])
                        rotate([0, 90, 0])
                            cylinder(h=square_shank_width/2, r=1);
        }

    // Threaded section
    translate([0, 0, -(shank_length + thread_length)])
        union() {
            // Main threaded cylinder
            cylinder(h=thread_length, d=bolt_diameter);

            // Simplified V-threads (printable without supports)
            threads_per_mm = 1.5;
