            for(i = [0 : 5 : thread_length-1]) {
                translate([0, 0, -(shank_length + i)])
                    cylinder(h=2, d=bolt_diameter + thread_depth);
                translate([0, 0, -(shank_length + i + 2)])
                    cylinder(h=2, d=bolt_diameter - thread_depth);
            }
        }

