    color("burlywood") {
        // Arrow shaft (main body)
        cylinder(h = 75, d = 3, center = false);

        // Shaft taper at nock end
        translate([0, 0, 72])
            cylinder(h = 3, d1 = 3, d2 = 2.5, center = false);
    }

    color("goldenrod") {
        // Arrowhead - leaf-shaped barbed point
        translate([0, 0, 75]) {
            // Central spine
            cylinder(h = 15, d = 2.5, center = false);

            // Barbs - two pairs at angles
            for(i = [0:1]) {
                rotate([0, 0, i * 180]) {
                    // Upper barbs
                    translate([1.5, 0, 10])
                        rotate([0, 90, 0])
                            cylinder(h = 4, d = 1, center = false);
                    translate([1.5, 0, 7])
                        rotate([0, 90, 0])
                            cylinder(h = 3.5, d = 0.8, center = false);

                    // Lower barbs
                    translate([-1.5, 0, 5])
                        rotate([0, -90, 0])
                            cylinder(h = 4, d = 1, center = false);
                    translate([-1.5, 0, 2])
                        rotate([0, -90, 0])
                            cylinder(h = 3.5, d = 0.8, center = false);
                }
            }

            // Leaf-shaped blade edges
            for(angle = [0, 120, 240]) {
                rotate([0, 0, angle])
                    translate([0, 0, 7.5])
                        rotate([90, 0, 0])
                            scale([1, 0.3, 1])
                                cylinder(h = 15, d = 8, center = true);
            }
        }
    }

    // Decorative bronze binding ring
    color("darkgoldenrod") {
        translate([0, 0, 68])
            cylinder(h = 2, d = 3.5, center = false);
    }

    // Fletching - three feathers
    color("dimgray") {
        for(i = [0:2]) {
            rotate([0, 0, i * 120]) {
                // Feather base
                translate([0, 1.5, 60])
                    rotate([90, 0, 0])
                        scale([1, 0.15, 1])
                            cylinder(h = 12, d = 3, center = true);

                // Feather barbs (simplified)
                for(pos = [2:2:10]) {
                    translate([0, 1.5, 60 - pos])
                        rotate([90, 0, 0])
                            scale([0.8 - pos/25, 0.1, 1])
                                cylinder(h = 10 - pos, d = 2.5, center = true);
                }
            }
        }
    }

    // Nock detail
    color("burlywood") {
        translate([0, 0, 0]) {
            difference() {
                cylinder(h = 5, d = 3, center = false);
                // Nock groove
                translate([0, -1.5, 2.5])
                    cube([3, 3, 2], center = true);
            }
        }
    }

    // Historical decorative spiral pattern on shaft
    color("saddlebrown") {
        for(z = [15:15:60]) {
            rotate([0, 0, z * 3])
                translate([1.6, 0, z])
                    rotate([90, 0, 0])
                        cylinder(h = 0.5, d = 0.8, center = true);
        }
