    color(bronze) {
        difference() {
            // Main socket cylinder
            cylinder(h=socket_length, d=socket_diameter, $fn=32);

            // Hollow interior
            translate([0, 0, -1])
                cylinder(h=socket_length+2, d=socket_diameter-4, $fn=32);

