            translate([0, 0, 8])
                cylinder(h=3, d=socket_diameter+2, $fn=32);
        }

        // Decorative raised ring
        color(highlight)
        translate([0, 0, 8])
            cylinder(h=2, d=socket_diameter+1, $fn=32);
    }
