// Blade with historical European profile
module blade() {
    color(dark_bronze) {
        union() {
            // Central diamond cross-section
            translate([0, 0, socket_length])
            linear_extrude(height=point_length, convexity=10)
            polygon(points=[
                [0, 0],
                [blade_span/2 - 8, 0],
                [blade_span/2, blade_span/4],
                [0, blade_span/2],
                [-blade_span/2, blade_span/4],
                [-blade_span/2 + 8, 0]
            ]);

            // Blade thickness (tapered)
            for(i = [0:1:3]) {
                rotate([0, 0, i*90])
                translate([0, 0, socket_length])
                linear_extrude(height=point_length, scale=[1, 0.3])
                polygon(points=[
                    [0, 0],
                    [blade_span/2 - 8, 0],
                    [blade_span/2 - 8 - i*2, blade_thickness],
                    [0, blade_thickness]
                ]);
            }

            // Barbs (historical style)
            for(side = [-1, 1]) {
                translate([side * (blade_span/2 - 5), 0, socket_length + 10])
                rotate([0, 0, side * 25])
                linear_extrude(height=2)
                polygon(points=[
                    [0, 0],
                    [5, 8],
                    [7, 8],
                    [2, 0]
                ]);
            }
        }
    }
}

