head_color = [0.7, 0.7, 0.7];  // Iron gray
fletch_color = [0.1, 0.1, 0.1];  // Black feathers

module shaft() {
    color(shaft_color)
    union() {
        // Main shaft
        cylinder(h=arrow_length, d=shaft_diameter, $fn=16);

        // Decorative ring near head
        translate([0, 0, arrow_length-5])
            cylinder(h=2, d=shaft_diameter+2, $fn=16);
    }
}

module arrowhead() {
    color(head_color)
    union() {
        // Arrow point
        translate([0, 0, arrow_length])
            cylinder(h=head_length, d1=0, d2=6, $fn=8);

        // Whistle chamber body
        translate([0, 0, arrow_length-head_length-whistle_length])
            cylinder(h=whistle_length, d=whistle_diameter, $fn=32);

        // Whistle holes (3 holes around circumference)
        for(i=[0:2]) {
            rotate([0, 0, i*120])
                translate([whistle_diameter/2 - whistle_wall/2, 0, arrow_length-head_length-whistle_length+whistle_length/2])
                    rotate([0, 90, 0])
                        cylinder(h=whistle_wall+0.2, d=2, $fn=8);
        }

        // Internal whistle cavity
        translate([0, 0, arrow_length-head_length-whistle_length+whistle_wall])
            cylinder(h=whistle_length-whistle_wall*2, d=whistle_diameter-whistle_wall*2, $fn=32);
    }
}

module fletching() {
    color(fletch_color)
    for(i=[0:2]) {
        rotate([0, 0, i*120+30])
            translate([0, fletch_width/2, fletch_length/2])
                cube([fletch_height, fletch_width, fletch_length], center=true);
    }
}

module nock() {
    color(shaft_color)
    difference() {
        cylinder(h=5, d=shaft_diameter+1, center=true, $fn=16);

        // Nock groove
        rotate([90, 0, 0])
            cube([nock_depth+1, nock_diameter, nock_diameter*1.5], center=true);
    }
}

// Assembly
union() {
    shaft();
    arrowhead();

    // Fletching at rear
    translate([0, 0, fletch_length/2])
        fletching();

    // Nock at very rear
    translate([0, 0, -arrow_length/2-2.5])
        nock();
}

