                    translate([i * scale_factor, 0, 10 * scale_factor])
                        scale([scale_factor * 0.1, scale_factor * 1.2, scale_factor])
                            rotate([90, 0, 0])
                                cylinder(h = 60, d1 = 5, d2 = 5);
