// Main colors
color("silver") lorica();
color("darkgoldenrod") trim();

module lorica() {
    difference() {
        // Main body
        scale([1, 0.8, 1.2]) sphere(d=60);

        // Neck opening
        translate([0, 0, 30]) rotate([90, 0, 0]) 
            cylinder(h=40, d1=25, d2=30, center=true);

        // Arm holes
        translate([0, 27, 8]) rotate([0, 90, 0])
            cylinder(h=30, d=25, center=true);
        translate([0, -27, 8]) rotate([0, 90, 0])
            cylinder(h=30, d=25, center=true);

        // Bottom opening
        translate([0, 0, -35]) cube([50, 50, 20], center=true);
    }

    // Mail texture - overlapping rings pattern
    for(i=[-30:5:30]) {
        for(j=[-15:5:15]) {
            if(i*i + j*j < 650) {
                translate([i, j, 18+sin(i*0.2)*2]) {
                    rotate([90, 0, 0]) 
                        torus_ring();
                }
            }
        }
    }

    // Back side rings
    for(i=[-30:5:30]) {
        for(j=[-15:5:15]) {
            if(i*i + j*j < 650) {
                translate([i, j, -18-sin(i*0.2)*2]) {
                    rotate([-90, 0, 0]) 
                        torus_ring();
                }
            }
        }
    }
}

module torus_ring() {
    difference() {
        cylinder(h=2, d=4);
        translate([0, 0, -0.5]) cylinder(h=3, d=2.5);
    }
}

