    // Shoulder reinforcements
    translate([0, 30, 10]) rotate([0, 90, 0])
        scale([1, 0.3, 0.3]) cylinder(h=50, d=8);
    translate([0, -30, 10]) rotate([0, 90, 0])
        scale([1, 0.3, 0.3]) cylinder(h=50, d=8);

