        translate([29, -35, 0]) 
            circle(d=3);
    }

    // Decorative studs
    for(a=[0:30:330]) {
        translate([25*cos(a), 25*sin(a), 30]) 
            sphere(d=2);
    }

    // Central decorative plate
    translate([0, 0, 25]) {
        difference() {
            cube([15, 20, 1], center=true);
            for(x=[-5:5:5]) {
                for(y=[-7:7:7]) {
                    translate([x, y, 0.5]) 
                        cylinder(h=2, d=1);
                }
            }
        }
    }
