// Ringmail fragment - 14th century European pattern
// Dimensions: 80mm x 60mm x 8mm

$fn = 32;

// Material colors
color_iron = [0.3, 0.3, 0.3];
color_rust = [0.5, 0.2, 0.1];

// Ring parameters
ring_diameter = 8;
wire_thickness = 1.2;
ring_spacing = 10;
rows = 6;
cols = 8;

// Create a single riveted ring
module riveted_ring() {
    difference() {
        // Main ring body
        color(color_iron)
        rotate_extrude()
            translate([ring_diameter/2, 0, 0])
                circle(wire_thickness/2);

        // Rivet head on top
        color(color_rust)
        translate([0, 0, ring_diameter/2])
            sphere(wire_thickness * 0.8);
    }

    // Rivet seam - slight gap in ring
    color(color_rust)
    rotate([90, 0, 0])
        translate([ring_diameter/2, 0, 0])
            cube([wire_thickness*1.5, 0.3, wire_thickness*1.5], center=true);
}

// Create alternating ring pattern (4-in-1 weave)
module ring_pattern() {
    for (row = [0:rows-1]) {
        for (col = [0:cols-1]) {
            // Offset every other row
            x_offset = (row % 2) * ring_spacing/2;
            y_offset = row * ring_spacing * 0.866;

