leather_color = [0.3, 0.2, 0.1];

// Individual scale module with historical Roman styling
module roman_scale(scale_size, thickness) {
    // Main scale body - slightly curved
    difference() {
        // Main scale plate
        scale([0.9, 1, 1])
        linear_extrude(height = thickness, center = true, convexity = 10)
        difference() {
            circle(d = scale_size);
            // Decorative cutout
            translate([0, -scale_size/4, 0])
            circle(d = scale_size * 0.3);
        }

        // Central ridge for strength
        translate([0, -scale_size/2, -thickness/2])
        cube([scale_size * 0.1, scale_size, thickness + 0.1], center = true);

        // Attachment holes
        translate([scale_size * 0.3, scale_size * 0.3, -thickness/2])
        cylinder(h = thickness + 0.2, r = 0.6, center = true);

        translate([-scale_size * 0.3, scale_size * 0.3, -thickness/2])
        cylinder(h = thickness + 0.2, r = 0.6, center = true);
    }
}

// Leather backing with stitching holes
module leather_backing(width, height, thickness) {
    color(leather_color)
    difference() {
        // Main backing
        translate([-width/2, -height/2, -thickness/2])
        cube([width, height, thickness]);

        // Stitching holes along edges
        for(x = [-width/2 + 5 : 10 : width/2 - 5]) {
            for(y = [-height/2 + 5 : 10 : height/2 - 5]) {
                if(abs(x) == width/2 - 5 || abs(y) == height/2 - 5) {
                    translate([x, y, -thickness])
                    cylinder(h = thickness + 1, r = 0.8);
                }
            }
        }
    }
}

// Complete scale row
module scale_row(row_length, y_pos, offset) {
    for(x = [-row_length/2 + scale_size/2 : scale_size : row_length/2 - scale_size/2]) {
        translate([x + offset, y_pos, scale_thickness/2 + leather_thickness])
        rotate([15, 0, 0])
        color(brass_color)
        roman_scale(scale_size, scale_thickness);
    }
}

// Main assembly
difference() {
    union() {
        // Leather backing
        leather_backing(segment_width, segment_height, leather_thickness);

        // Scale rows - historical overlapping pattern
        for(row = [0 : 6]) {
            y_pos = -segment_height/2 + row * (scale_size * 0.7) + scale_size/2;
            offset = (row % 2) * scale_size/2;
            scale_row(segment_width - 10, y_pos, offset);
        }

        // Decorative border rivets
        for(x = [-segment_width/2 + 5 : 10 : segment_width/2 - 5]) {
            for(y = [-segment_height/2 + 5 : 10 : segment_height/2 - 5]) {
                if(abs(x) == segment_width/2 - 5 || abs(y) == segment_height/2 - 5) {
                    translate([x, y, leather_thickness + scale_thickness + 0.5])
                    color(brass_color)
                    cylinder(h = 1, r = 1.5);
                }
            }
        }
    }

    // Keyhole for display mounting
    translate([0, segment_height/2 - 10, -leather_thickness])
    cylinder(h = leather_thickness + 1, r = 3);
    translate([0, segment_height/2 - 10, -leather_thickness - 1])
    cube([6, 6, leather_thickness + 2], center = true);
}

