    // Curved handle typical of Bronze Age mirrors
    color("Bronze")
    union() {
        // Main handle shaft
        linear_extrude(height=handle_length, scale=[1, 0.7], twist=45)
            circle(d=8*scale_factor);

        // Decorative collar at mirror junction
        translate([0, 0, handle_length-2*scale_factor])
            cylinder(h=2*scale_factor, d1=10*scale_factor, d2=8*scale_factor);

        // Ring terminal
        translate([0, 0, -1])
            handle_ring();
    }
}

module decorative_border() {
    // Concentric rings with dot pattern typical of Bronze Age decoration
    color("Bronze")
    difference() {
        // Raised border ring
        cylinder(h=wall_thickness*0.8, d=70*scale_factor);

        // Recess for dot pattern
        cylinder(h=wall_thickness*0.8 + 0.1, d=68*scale_factor);
    }

    // Dot pattern
    for (a = [0:30:359]) {
        rotate([0, 0, a])
            translate([33*scale_factor, 0, wall_thickness*0.4])
                sphere(d=1.5*scale_factor);
    }
}

module mirrorplate() {
    // Main mirror surface with characteristic Bronze Age proportions
    color("Bronze")
    difference() {
        // Overall disc shape
        cylinder(h=wall_thickness, d=70*scale_factor);

        // Recessed mirror surface
        translate([0, 0, -0.1])
            cylinder(h=wall_thickness*0.5, d=65*scale_factor);

        // Central dimple (common on period mirrors)
        translate([0, 0, -0.2])
            sphere(d=8*scale_factor);
    }

    // Attach handle
    translate([0, 35*scale_factor, wall_thickness])
        rotate([180, 0, 0])
            handle();

    // Add decorative border
    translate([0, 0, wall_thickness*0.2])
        decorative_border();
}

