// Mirror armor (Kusari Katabira) - Japanese chain mail with small iron plates
// Historical artifact from Edo period Japan (1603-1868)

// Overall dimensions
overall_width = 60;
overall_height = 80;
plate_size = 6;
plate_thickness = 1;
chain_link_size = 2;
chain_link_thickness = 0.4;

// Define colors
iron_color = [0.3, 0.3, 0.32];
copper_color = [0.72, 0.45, 0.2];

// Module for single rectangular plate with slightly rounded corners
module mirror_plate(size, thickness) {
    difference() {
        // Main plate
        cube([size, size * 0.7, thickness], center = true);

        // Rounded edges by subtracting cylinders
        translate([size/2 - thickness, 0, thickness/2])
            rotate([0, 90, 0])
                cylinder(h = size * 0.7, r = thickness/2, $fn = 8);
        translate([-size/2 + thickness, 0, thickness/2])
            rotate([0, 90, 0])
                cylinder(h = size * 0.7, r = thickness/2, $fn = 8);
    }
}

// Module for chain link (simple torus)
module chain_link(size, thickness) {
    rotate([90, 0, 0])
        difference() {
            cylinder(h = size, r = thickness, $fn = 12);
            translate([0, 0, -0.1])
                cylinder(h = size + 0.2, r = thickness/2, $fn = 12);
        }
}

// Module for copper rivet decoration
module rivet() {
    color(copper_color) {
        translate([0, 0, plate_thickness/2 + 0.3])
            cylinder(h = 0.6, r = 0.5, $fn = 6);
    }
}

// Module for single armor segment (plate + 4 chain links + rivets)
module armor_segment(x, y) {
    translate([x, y, 0]) {
        color(iron_color) {
            mirror_plate(plate_size, plate_thickness);
        }

        // Decorative rivets at corners
        translate([plate_size/2 - 1, plate_size * 0.7/2 - 1, 0]) rivet();
        translate([-plate_size/2 + 1, plate_size * 0.7/2 - 1, 0]) rivet();
        translate([plate_size/2 - 1, -plate_size * 0.7/2 + 1, 0]) rivet();
        translate([-plate_size/2 + 1, -plate_size * 0.7/2 + 1, 0]) rivet();

        // Chain links at corners
        translate([plate_size/2 + chain_link_size/2, plate_size * 0.7/2 + chain_link_size/2, 0]) 
            color(iron_color) chain_link(chain_link_size, chain_link_thickness);
        translate([-plate_size/2 - chain_link_size/2, plate_size * 0.7/2 + chain_link_size/2, 0]) 
            color(iron_color) chain_link(chain_link_size, chain_link_thickness);
        translate([plate_size/2 + chain_link_size/2, -plate_size * 0.7/2 - chain_link_size/2, 0]) 
            color(iron_color) chain_link(chain_link_size, chain_link_thickness);
        translate([-plate_size/2 - chain_link_size/2, -plate_size * 0.7/2 - chain_link_size/2, 0]) 
            color(iron_color) chain_link(chain_link_size, chain_link_thickness);
    }
}

// Main armor assembly
difference() {
    union() {
        // Create grid of armor segments
        for (x = [-overall_width/2 + plate_size/2 : plate_size + chain_link_size : overall_width/2 - plate_size/2]) {
            for (y = [-overall_height/2 + plate_size * 0.35 : plate_size * 0.7 + chain_link_size : overall_height/2 - plate_size * 0.35]) {
                armor_segment(x, y);
            }
        }

        // Central decorative plate (larger)
        translate([0, 0, 0.5]) {
            color(iron_color) {
                scale([1.2, 1.2, 1]) mirror_plate(plate_size * 1.5, plate_thickness + 0.5);
            }

            // Family mon (crest) - simple circle
            translate([0, 0, plate_thickness + 0.5]) {
                color(copper_color) {
                    difference() {
                        cylinder(h = 0.5, r = 3, $fn = 32);
                        cylinder(h = 0.6, r = 2, $fn = 32);
                    }
                }
            }
        }
    }

    // Cut neck opening
    translate([0, overall_height/2 - 10, -1])
        cylinder(h = plate_thickness + 2, r = 8, $fn = 32);

    // Cut arm holes
    translate([-overall_width/2 + 8, 0, -1])
        rotate([0, 0, 30])
            cylinder(h = plate_thickness + 2, r = 6, $fn = 32);
    translate([overall_width/2 - 8, 0, -1])
        rotate([0, 0, -30])
            cylinder(h = plate_thickness + 2, r = 6, $fn = 32);
}

// Brigandine Coat - 15th Century Style
