    for(i = [-coat_width/2 + 5 : 5 : coat_width/2 - 5]) {
        color(brass_color)
        translate([i, -coat_length/2 + 5, 3])
        cube([3, 3, 1]);
    }
    for(i = [-coat_length/2 + 10 : 5 : coat_length/2 - 5]) {
        color(brass_color)
        translate([-coat_width/2 + 2.5, i, 3])
        cube([3, 3, 1]);
    }
    for(i = [-coat_length/2 + 10 : 5 : coat_length/2 - 5]) {
        color(brass_color)
        translate([coat_width/2 - 2.5, i, 3])
        cube([3, 3, 1]);
