// Coat of Plates - Historical Artifact (14th century style)
// Realistic medium complexity model for 3D printing

// Overall dimensions
plate_width = 45;
plate_height = 60;
plate_thickness = 2.5;
rivet_size = 1.2;
leather_thickness = 1;

module rivet() {
    // Simple rivet with rounded head
    union() {
        cylinder(h=leather_thickness + 2, r=rivet_size/2, $fn=8);
        translate([0, 0, leather_thickness + 2]) 
            cylinder(h=1, r=rivet_size/1.5, $fn=8);
    }
}

module single_plate(w, h, has_rivets=true) {
    difference() {
        // Main rectangular plate with rounded edges
        union() {
            cube([w, h, plate_thickness]);
            // Rounded top edge
            translate([w/2, h-plate_thickness/2, plate_thickness/2])
                rotate([90, 0, 0])
                    cylinder(h=w, r=plate_thickness/2, $fn=16);
            // Rounded bottom edge
            translate([w/2, plate_thickness/2, plate_thickness/2])
                rotate([90, 0, 0])
                    cylinder(h=w, r=plate_thickness/2, $fn=16);
        }

        // Central ridge groove
        translate([w/2 - 1, 0, -0.1])
            cube([2, h, plate_thickness + 0.2]);
    }

    // Add rivets at corners if needed
    if (has_rivets) {
        translate([3, 3, plate_thickness])
            rivet();
        translate([w-3, 3, plate_thickness])
            rivet();
        translate([3, h-3, plate_thickness])
            rivet();
        translate([w-3, h-3, plate_thickness])
            rivet();
    }
}

module backplate() {
    // Leather/fabric backing
    color([0.4, 0.25, 0.1])  // Dark brown leather
    translate([0, 0, -leather_thickness])
        cube([plate_width + 10, plate_height + 20, leather_thickness]);
}

module coat_of_plates() {
    backplate();

    color([0.7, 0.7, 0.7])  // Dark steel color

    // Top row - smaller plates
    for (i = [0:2]) {
        translate([i * (plate_width/3 + 2) + 2, plate_height - 15, 0])
            single_plate(plate_width/3, 15, true);
    }

    // Middle rows - larger plates
    for (row = [0:3]) {
        for (col = [0:1]) {
            translate([col * (plate_width/2 + 2) + 2, 
                      plate_height - 30 - row * 15, 0])
                single_plate(plate_width/2 - 1, 15, true);
        }
    }

    // Bottom row - tapered plates
    for (i = [0:3]) {
        translate([i * (plate_width/4 + 1) + 2, 5, 0])
            single_plate(plate_width/4, 10, true);
    }

    // Side plates for wrap-around protection
    color([0.6, 0.6, 0.6])
    translate([-3, 20, 0])
        rotate([0, 0, -15])
            single_plate(8, 30, false);
    translate([plate_width + 3, 20, 0])
        rotate([0, 0, 15])
            single_plate(8, 30, false);

    // Decorative central boss
    color([0.8, 0.7, 0.3])  // Brass color
    translate([plate_width/2, plate_height/2, plate_thickness + leather_thickness])
        cylinder(h=2, r=5, $fn=32);
    translate([plate_width/2, plate_height/2, plate_thickness + leather_thickness + 2])
        cylinder(h=1, r=3, $fn=32);
}

// Main assembly
coat_of_plates();

// Jack of Plate - 16th century body armor
// A jack of plate was a type of armor consisting of small iron plates sewn between layers of fabric
// This represents a typical example from the Tudor period, scaled for 3D printing

// Overall dimensions
overall_length = 80;
overall_width = 60;
overall_thickness = 8;

// Plate dimensions
plate_diameter = 12;
plate_thickness = 1.5;
plate_spacing = 15;

// Fabric layers
fabric_thickness = 1.5;
inner_layer_offset = 1;
outer_layer_offset = 2;

// Decorative elements
rivet_diameter = 2;
rivet_height = 1;
border_width = 5;

// Colors
color_plate = [0.4, 0.4, 0.4];  // Iron gray
color_fabric = [0.3, 0.2, 0.1];  // Dark brown canvas
color_rivets = [0.5, 0.5, 0.5];  // Steel gray

module single_plate() {
    difference() {
        // Main plate
        cylinder(h = plate_thickness, d = plate_diameter, $fn = 32);

        // Central hole
        cylinder(h = plate_thickness + 0.1, d = rivet_diameter, $fn = 16);
    }
}

module plate_array() {
    color(color_plate) {
        for (x = [-overall_width/2 + plate_spacing : plate_spacing : overall_width/2 - plate_spacing]) {
            for (y = [-overall_length/2 + plate_spacing : plate_spacing : overall_length/2 - plate_spacing]) {
                translate([x, y, overall_thickness/2 - plate_thickness/2 - fabric_thickness]) {
                    single_plate();
                }
            }
        }
    }
}

module fabric_layer(z_offset) {
    color(color_fabric) {
        // Main rectangular piece
        translate([0, 0, overall_thickness/2 - z_offset]) {
            cube([overall_width, overall_length, fabric_thickness], center = true);
        }

        // Rounded corners for historical accuracy
        difference() {
            translate([0, 0, overall_thickness/2 - z_offset]) {
                cube([overall_width - border_width, overall_length - border_width, fabric_thickness], center = true);
            }

            // Decorative scalloped edge pattern
            for (i = [0:5:overall_width - border_width]) {
                translate([i - overall_width/2 + border_width/2, -overall_length/2 + border_width/2, overall_thickness/2 - z_offset]) {
                    cylinder(h = fabric_thickness + 0.1, d = 6, $fn = 16);
                }
                translate([i - overall_width/2 + border_width/2, overall_length/2 - border_width/2, overall_thickness/2 - z_offset]) {
                    cylinder(h = fabric_thickness + 0.1, d = 6, $fn = 16);
                }
            }
        }
    }
}

module rivets() {
    color(color_rivets) {
        // Rivets at plate intersections
        for (x = [-overall_width/2 + plate_spacing : plate_spacing : overall_width/2 - plate_spacing]) {
            for (y = [-overall_length/2 + plate_spacing : plate_spacing : overall_length/2 - plate_spacing]) {
                translate([x, y, overall_thickness/2 + fabric_thickness/2]) {
                    cylinder(h = rivet_height, d = rivet_diameter, $fn = 16);
                }
            }
        }

        // Border rivets
        for (angle = [0:30:359]) {
            translate([
                (overall_width/2 - border_width/2) * cos(angle),
                (overall_length/2 - border_width/2) * sin(angle),
                overall_thickness/2 + fabric_thickness/2
            ]) {
                cylinder(h = rivet_height, d = rivet_diameter * 0.7, $fn = 16);
            }
        }
    }
}

module shoulder_straps() {
    color(color_fabric) {
        // Left shoulder strap
        translate([-overall_width/2 - 8, overall_length/4, overall_thickness/2]) {
            cube([16, 8, fabric_thickness * 2], center = true);
        }

        // Right shoulder strap
        translate([overall_width/2 + 8, overall_length/4, overall_thickness/2]) {
            cube([16, 8, fabric_thickness * 2], center = true);
        }

        // Reinforcement plates on straps
        color(color_plate) {
            for (offset = [-5, 5]) {
                translate([-overall_width/2 - 8, overall_length/4 + offset, overall_thickness/2 + fabric_thickness]) {
                    cube([12, 3, plate_thickness], center = true);
                }
                translate([overall_width/2 + 8, overall_length/4 + offset, overall_thickness/2 + fabric_thickness]) {
                    cube([12, 3, plate_thickness], center = true);
                }
            }
        }
    }
}

// Main assembly
union() {
    // Inner fabric layer
    fabric_layer(inner_layer_offset);

    // Protective plates
    plate_array();

    // Outer fabric layer
    fabric_layer(outer_layer_offset);

    // Rivets holding everything together
    rivets();

    // Shoulder attachment straps
    shoulder_straps();
}

// Padded Gambeson  14th C. Western Europe
// 1 mm walls, no over-45 overhangs, 80 mm tall overall
// Colors: unbleached linen (#F5F5DC), stitched seams (#8B4513), iron buckles (#696969)

module gambeson() {
    // Central torso plate  lightly flared toward waist
    skin = 1.5;
    torso_h = 45;
    torso_w = 28;
    difference() {
        cube([torso_w, 20, torso_h], center=true);
        translate([0, 0, -skin])
            cube([torso_w - 2*skin, 20 - 2*skin, torso_h + skin], center=true);
    }

    // Horizontal quilting rows
    stitches = 8;
    color("#8B4513")
    for (i=[1:stitches-1]) {
        translate([0, 0, -torso_h/2 + i*torso_h/stitches])
            cube([torso_w + 2, 20.2, 0.4], center=true);
    }

    // Vertical side gussets for arm mobility
    gusset_w = 5;
    for (x=[-1, 1]) {
        translate([x*(torso_w/2 + gusset_w/2), 0, 0])
            cube([gusset_w, 20, torso_h], center=true);
    }

    // Shoulder straps with buckle
    strap_w = 4;
    strap_h = 12;
    translate([0, 0, torso_h/2 + strap_h/2 - 2])
        cube([torso_w, strap_w, strap_h], center=true);

    // Buckle plate
    color("#696969")
    translate([torso_w/2 + 1, 0, torso_h/2 + strap_h/2 - 2])
    rotate([0, 90, 0])
        cylinder(h=3, d=4, $fn=16);

    // Decorative edging strip (thin raised band)
    edging_h = 1;
    color("#D2B48C")
    translate([0, 0, -torso_h/2 - edging_h/2])
        cube([torso_w + 2, 22, edging_h], center=true);
}

