color_cord = [0.4, 0.3, 0.2];

// Main body module
module aketon_body() {
    color(color_linen) {
        difference() {
            // Main padded body
            translate([0, 0, thickness/2])
                cube([length, width, thickness], center=true);

            // Neck opening
            translate([0, width/3, thickness/2])
                rotate([0, 0, 0])
                cylinder(h=thickness+2, r=10, center=true);

            // Arm holes (elliptical)
            for(side = [-1, 1]) {
                translate([side * length/3, -width/2 - 2, thickness/2])
                    rotate([90, 0, 0])
                    scale([1, 1.5, 1])
                    cylinder(h=5, r=8, center=true);
            }
        }
    }
}

// Quilted pattern module
module quilting_pattern() {
    color(color_stitching) {
        // Vertical quilting lines
        for(x = [-length/2 + 5 : 10 : length/2 - 5]) {
            translate([x, 0, thickness + 0.1])
                cube([0.5, width, 0.2]);
        }

        // Horizontal quilting lines
        for(y = [-width/2 + 5 : 10 : width/2 - 5]) {
            translate([0, y, thickness + 0.1])
                cube([length, 0.5, 0.2]);
        }
    }
}

// Decorative edging cords module
